// Update input values
var inState = Engine_updateInput( nodeData, name, value );
var state = JSON.parse(Engine_getInput( nodeData, 'state', '{}' ));

// True if all inputs arrived
if( inState.isCompute ) {
  const info = checkIfCommandIsValid( value );
  if( info.isValid ) {
    Engine_fireOutput( nodeData, 'simCommand', info.command );
  } else {
    Engine_fireOutput( nodeData, 'feedback', info.errorMsg );
  }
}

// TODO: Test 2 strategies: 
// Strategy1: just give an error stating the reason of failure
// Strategy2: same as 1 + suggest a command to do or a reasonning to apply
// Also check object affordances for each command
// Abstract commands like open instead of screw or open_door to get minimal 
//   representation for different abstraction level

function checkIfCommandIsValid( value ) {
  const toRobi = '[Roco to Robi] ';
  const toRoco = '[Robi to Roco] ';
  const idx = value.indexOf(toRobi);
  let errorMsg = '';
  let isValid = true;
  let command = '';
  if( idx != -1 ) {
    command = value.substring( idx + toRobi.length ).trim();
    if( command ) {
      isValid = true;
    } else {
      isValid = false;
      errorMsg = `${toRoco}Error: no command received.`;
    }
  } else {
    isValid = false;
    errorMsg = `${toRoco}Error: wrong command syntax. The command must have the syntax: ${toRobi}<command>.`;
  }
  const tokenList = command.split( ' ' );
  if( tokenList && isValid ) {
    const commandName = tokenList[0] || '';
    const objectName1 = tokenList[1] || '';
    const objectName2 = tokenList[2] || '';
    const handName = objectName2;
    switch( commandName ) {    
      case 'put':
        if( isBothHandEmpty() ) {
          errorMsg = `${toRoco}Error: cannot ${command} because both hands are empty.`;
          isValid = false;
        } else if( !isObjectExist( objectName1 ) ) {
          errorMsg = `${toRoco}Error: cannot ${command} because ${objectName1} does not exist in environment state.`;
          isValid = false;
        } else if( !isObjectInOneHand( objectName1 ) ) {
          errorMsg = `${toRoco}Error: cannot ${command} because ${objectName1} is not in a hand.`;
          isValid = false;         
        } else if( !isObjectExist( objectName2 ) ) {
          errorMsg = `${toRoco}Error: cannot ${command} because ${objectName2} does not exist in environment state.`;
          isValid = false;
        }
        break;
      case 'get':
        if( isBothHandFull() ) {
          errorMsg = `${toRoco}Error: cannot ${command} because both hands are already full with objects.`;
          isValid = false;
        } else if( !isObjectExist( objectName1 ) ) {
          errorMsg = `${toRoco}Error: cannot ${command} because ${objectName1} does not exist in environment state.`;
          isValid = false;
        } else if( !isObjectExist( handName ) ) {
          errorMsg = `${toRoco}Error: cannot ${command} because ${handName} is not the name of a hand.`;
          isValid = false;         
        } else if( !isHandEmpty( handName ) ) {FF
          const objectInHand = getObjectInHand( handName );
          errorMsg = `${toRoco}Error: cannot ${command} because ${handName} already contains ${objectInHand}.`;
          isValid = false;
        }
        break;
      case 'poor':
        if( !isObjectExist( objectName1 ) ) {
          errorMsg = `${toRoco}Error: cannot ${command} because ${objectName1} does not exist in environment state.`;
          isValid = false;
        } else if( !isObjectInOneHand( objectName1 ) ) {
          errorMsg = `${toRoco}Error: cannot ${command} because ${objectName1} is not in a hand.`;
          isValid = false;
        } else if( !isObjectExist( objectName2 ) ) {
          errorMsg = `${toRoco}Error: cannot ${command} because ${objectName2} does not exist in environment state.`;
          isValid = false;
        }
        break;
      case 'screw':
        if( !isObjectExist( objectName1 ) ) {
          errorMsg = `${toRoco}Error: cannot ${command} because ${objectName1} does not exist in environment state.`;
          isValid = false;
        } else if( !isObjectInOneHand( objectName1 ) ) {
          errorMsg = `${toRoco}Error: cannot ${command} because ${objectName1} is not in a hand.`;
          isValid = false;
        } else if( !isOneHandEmpty() ) {
          const busyHand = getHandNotHoldingObject( objectName1 );
          const objectInHand = getObjectInHand( busyHand );
          errorMsg = `${toRoco}Error: cannot ${command} because ${busyHand} is not free it holds ${objectInHand}.`;
          isValid = false;
        } else if( !isObjectOpenable() ) {
          errorMsg = `${toRoco}Error: cannot ${command} because ${objectName1} is not an object you can open by screwing it.`;
          isValid = false;
        }
        break;
      case 'open_door':
        if( !isObjectExist( objectName1 ) ) {
          errorMsg = `${toRoco}Error: cannot ${command} because ${objectName1} does not exist in environment state.`;
          isValid = false;
        } else if( !isObjectOpenable() ) {
          errorMsg = `${toRoco}Error: cannot ${command} because ${objectName1} has no door to open.`;
          isValid = false;
        } else if( isObjectOpened() ) {
          errorMsg = `${toRoco}Error: cannot ${command} because ${objectName1} is already opened.`;
          isValid = false;
        } else if( !isHandEmpty( handName ) ) {
          errorMsg = `${toRoco}Error: cannot ${command} because ${handName} is not empty.`;
          isValid = false;
        }
        break;
      case 'close_door':
        if( !isObjectExist( objectName1 ) ) {
          errorMsg = `${toRoco}Error: cannot ${command} because ${objectName1} does not exist in environment state.`;
          isValid = false;
        } else if( !isObjectOpenable() ) {
          errorMsg = `${toRoco}Error: cannot ${command} because ${objectName1} has no door to close.`;
          isValid = false;
        } else if( !isObjectOpened() ) {
          errorMsg = `${toRoco}Error: cannot ${command} because ${objectName1} is already closed.`;
          isValid = false;
        } else if( !isHandEmpty( handName ) ) {
          errorMsg = `${toRoco}Error: cannot ${command} because ${handName} is not empty.`;
          isValid = false;
        }
        break;
      case 'gaze':
        if( !isObjectExist( objectName1 ) ) {
          errorMsg = `${toRoco}Error: cannot ${command} because ${objectName1} does not exist in environment state.`;
          isValid = false;
        }
        break;
    }
  }
  return( { command, isValid, errorMsg } );
}

function isObjectExist( objectName ) {
  return( state[objectName] != undefined );
}
function isHandEmpty( handName ) {
  return( state[handName].holdsObject.length == 0 );
}
function isOneHandEmpty() {
  return ( isHandEmpty( 'hand_right' ) || isHandEmpty( 'hand_left' ) );
}
function isBothHandEmpty() {
  return ( isHandEmpty( 'hand_right' ) && isHandEmpty( 'hand_left' ) );
}
function isBothHandFull() {
  return ( !isHandEmpty( 'hand_right' ) && !isHandEmpty( 'hand_left' ) );
}
function isObjectInHand( handName, objectName ) {
  return( state[handName].holdsObject.includes( objectName ) );
}
function isObjectInOneHand( objectName ) {
  return( isObjectInHand( 'hand_right', objectName ) || isObjectInHand( 'hand_left', objectName ) );
}
function isObjectOpenable( objectName ) {
  return( state[objectName].closure != undefined );
}
function isObjectOpened( objectName ) {
  return( state[objectName].closure == 'opened'? true: false );
}
function getEmptyHand() {
  if( isHandEmpty( 'hand_right' ) ) {
    return( 'hand_right' );
  } else if( isHandEmpty( 'hand_left' ) ) {
    return( 'hand_left' );
  } else {
    return( '' );
  }
}
function getHandNotHoldingObject( objectName ) {
  if( isObjectInHand( 'hand_right', objectName ) ) {
    return( 'hand_left' );
  } else if ( isObjectInHand( 'hand_left', objectName ) ) {
    return( 'hand_right' );
  } else {
    return( '' );
  }
}
function getObjectInHand( handName ) {
  return( state[objectName].holdsObject[0] );
}

