{"label":"JSON Env to Text","category":"DataFlow_Component","size":"240 80","color":"LightSeaGreen","in_":[{"portId":"env","name":"env"}],"out_":[{"portId":"text","name":"text"}],"props_":[{"name":"computeBarrier","value":"","unit":"","valueChanged":true}],"isFile":true,"fileContent":"var nodeData;

const env = Engine_getInput( nodeData, 'env', '{}' );

const envObj = JSON.parse( env );

const objTypeList = new Set();
const ingredientList = new Set();
const bottleList = new Set();
const liquidList = new Set();

for( const name in envObj ) {
  const obj = envObj[name];
  const type = obj.type;

  switch( type ) {
    case 'room':
      break;
    case 'robot':
    case 'hand':
      break;
    case 'furniture':
      break;
    case 'liquid':
      liquidList.add( name );
      break;
    case 'bottle':
      bottleList.add( name );
      break;
    case 'ingredient':
      ingredientList.add( name );
      break;
    case 'bowl':
      break;
    default:
      if( type ) {
        objTypeList.add( type );
      }
  }
}

const text = 
`Current information about the environemnt state:
 - List of types of objects present: ${toStrList( objTypeList )}
 - List of ingredients: ${toStrList( ingredientList )}
 - List of bottle: ${toStrList( bottleList )}
 - List of liquid ingredients: ${toStrList( liquidList )}
 `;

Engine_fireOutput( nodeData, 'text', text );

function toStrList( data ) {
  const arrData = Array.from( data );
  const result = arrData.join( ', ' );
  return( result );
}","fileTypeName":"Component","fileType":"text/javascript","iconURL":"","key":12,"location":"5990 1689.9","editorPosition":[763,2035,605,890]},
