function generateGroup( gData ) {
  const gDataKey = gData.key;
  const dataList = graphData.me.getNodeListIf( graphData.modelId, 
                                             (d)=> d.group == gDataKey );
  for( const data of dataList ) {
    if( data.isGroup ) {
      if( Engine_isNodeEnabled( data ) ) {
        // Count dir
        ++graphData.count.dirCount;
        // Generate group
        generateGroup( data );
      }
    // } else if( data.category.startsWith( 'Hierarchy_CodeIn' ) ||
    //           ( data.category == 'CodeFlow_Message' ) ) {
    } else if( !data.out_ || data.out_.lenght == 0 ) {
      // Generate File

      traverseNode( data );
    }
  }
}
function traverseNode( data ) {
  // Get input link into data node
  const inDataList = graphData.me.getNodeListFanInByNodeKey( graphData.modelId,
                                                             data.key );
  // Loop over in nodes
  const demultiplexerVisited = {};
  for( const inData of inDataList ) {
    if( Engine_isNodeEnabled( inData ) ) {

      // Skip already visited demultiplexer
      const isDemultiplexer = ( inData.category == 'CodeFlow_Demultiplexer' );
      if( isDemultiplexer && demultiplexerVisited[inData.key] ) {
        continue;
      }
      // Visit node
      traverseNode( inData );

      // Save demultiplexer visit
      if( isDemultiplexer ) {
        demultiplexerVisited[inData.key] = true;
      }
    }
  }

  const isFile = ( data.category.startsWith( 'Hierarchy_CodeIn') );
  if( isFile ) {
    // Count file
    ++graphData.count.fileCount;
  } else {
    // Count nodes
    ++graphData.count.nodeCount;
  }

  // Execute doCompute
  console.log( 'doCompute [', data.key, ']', data.label || data.category );
  Engine_doCompute( data );
}","fileTypeName":"Javascript","fileType":"text/javascript","key":19,"location":"2080 -770","isIncludeScript":true,"editorPosition":[1114,908,636,746]},
