function CodeFlow_Code_doCompute( data ) {
  // Get node instance
  const ni = Engine_getNodeInstance( data );
  // Make sure all input have a value
  const inputMap = Engine_getInputValueMap( data, null );

  // Process template
  graphData.lbg.setLanguage( 'text' );
  const template = data.fileContent;
  graphData.lbg.setTemplate( template );
  graphData.lbg.process( inputMap );

  // Generate all outputs
  const outNameList = Engine_getOutputNameList( data, true );
  for( const outName of outNameList ) {
    const value = graphData.lbg.extractLineBlock( outName, true ); // Trim indentation
    Engine_fireOutput( data, outName, value );
  }
}
function CodeFlow_Merge_doCompute( data ) {
  const value = [];
  // Get list of input names
  const inNameList = Engine_getInputNameList( data );
  for( const inName of inNameList ) {
    const inValue = Engine_getInput( data, inName, null );
    if( inValue ) {
      value.push( ...inValue );
    }
  }

  Engine_fireOutput( data, 'out', value );
}
function Hierarchy_CodeInFile_doCompute( data ) {
  if( Engine_isInputConnected( data ) ) {
    // Make sure all input have a value
    Engine_setInputDefault( data, '' );
    // Get input value
    const value = Engine_getInput( data, '*', '' );
    // Get a copy of the data (avoid changing original data)
    const dataCopy = getNodeData( data.key, true );
    // Set file content to be saved
    dataCopy.fileContent = ( Array.isArray( value )? value.join( '\n' ): value );
    saveNodeContent( dataCopy, ()=> console.log( '***Data Saved***' ) );
  }
}
function Hierarchy_CodeInGraph_doCompute( data ) {
  Hierarchy_CodeInFile_doCompute( data );
}
function CodeFlow_Multiplexer_doCompute( data ) {
  /*
   * outValue will be in the form:
   * {
   *   'title':   [ { value: 'titleValue' }, ... ],
   *   'include': [ { value: 'includeValue' }, ... ],
   *   'script':  [ { value: 'scriptValue' }, ... ],
   * }
   */
  const outValue = {};

  // Get list of input names
  const inNameList = Engine_getInputNameList( data );
  for( const inName of inNameList ) {
    const inValue = Engine_getInput( data, inName );
    //const inValueRef = Engine_getInputRef( data, inName );

    if( inValue != undefined ) { // If input is connected
    //if( inValueRef != undefined ) { // If input is connected
      // Check input type: bus or array of strings
      if( inName.startsWith( '_bus' ) ) {  // Case of bus input
        const busValue = inValue;
        //const busValue = inValueRef.value;
        // Here we append refValues in each Result name field array
        for( const bInName in busValue ) {
          // Create output name entry
          if( !outValue[bInName] ) {
            outValue[bInName] = [];
          }
          const bInValueList = busValue[bInName];
          outValue[bInName].push( ...bInValueList );
        }
      } else {                             // Case of array of strings
        // Create output name entry
        if( !outValue[inName] ) {
          outValue[inName] = [];
        }
        outValue[inName].push( inValue );
        //outValue[inName].push( inValueRef );
      }
    }
  }
  Engine_fireOutput( data, 'out', outValue );
}
function CodeFlow_Demultiplexer_doCompute( data ) {
  // Get node instance
  const ni = Engine_getNodeInstance( data );
  // Get input
  const inValueData = Engine_getInput( data, 'in', {} );

  // Concatenate all values
  for( const inName in inValueData ) {
    const outValue = [];
    const inValueList = inValueData[inName];
    inValueList.forEach( (v)=> {
      outValue.push( ...v );
      //outValue.push( ...v.value );
    });
    Engine_fireOutput( data, inName, outValue ); 
  }
}
function CodeFlow_Message_doCompute( data ) {
  // Get node instance
  const ni = Engine_getNodeInstance( data );
  // Get input
  let inValue = Engine_getInput( data, 'in', null );

  if( typeof( inValue ) == 'object' ) {
    inValue = JSON.stringify( inValue, null, 2 );
  }
  setNodeDataField( data.key, 'label', inValue )
}
