{"label":"Loader Test Page","color":"lightsalmon","size":"213 89","category":"Hierarchy_CodeInGraph","isFile":true,"fileContent":"<!DOCTYPE html>
<html>
<head>
  <meta charset='utf-8'>
  <title>Test WebPage</title>
  <style>
  </style>
  <script>

    const p = {
      //[# Begin pDeclaration #]
      v_cwd: '/',
      //[# End pDeclaration #]
    };

    const t = {
      //[# Begin tDeclaration #]
      v_VFSRoot: {
        __dirName: '',
        __prevDirObj: null,
        'ContextMenu': {
          'DiagramMenu': {},
          'NodeMenu': {},
        },
        'Diagram': {
          'getDiagramURL': Diagram_getDiagramURL,
          'getProperty': Diagram_getProperty,
          'getDataInfoFromSelection': Diagram_getDataInfoFromSelection,
          'selectNodeByKey': Diagram_selectNodeByKey,
        },
        'Loader': {},
        'State': {
          'getStatusName': State_getStatusName,
        },
        'Window': {
          'WindowList': {
            'Selection': {
              'closeWindow': Window_closeWindow,
            },
            'System Monitor': {
              'closeWindow': Window_closeWindow,
            },
            'VFS Actions': {
              'closeWindow': Window_closeWindow,
            }
          },
          'openWindowFromSelection': Window_openWindowFromSelection,
        },
      },
      v_cwdObj: null,
      //[# End tDeclaration #]
    };

    function Diagram_getDiagramURL() {
      return( 'localhost' );
    }
    function Diagram_getProperty() {
      return( `Model Info:\n`+
    	        `- #20 nodes\n`+
    	        `- #32 links\n`+
    	        `WorkSpace URL: localhost` );

    }
    function Diagram_getDataInfoFromSelection() {
      return( `Node [13]: Diagram Action (Link)\n`+
              `Category: CodeFlow_Code\n`+
              `Member of group: [7]` );
    }
    function Diagram_selectNodeByKey() {
      return( 'Node selected' );
    }
    function State_getStatusName() {
      return( 'ready' );
    }
    function Window_closeWindow() {
      return( 'window closed' );
    }
    function Window_openWindowFromSelection() {
      return( 'Window opened' );
    }

    //[# Begin section #]
    //[# Begin scriptOnLoad #]
    //[# End scriptOnLoad #]

    window.addEventListener( 'load', ()=>{
      // Set initialization function
      document.body.style.setProperty( 'background', '#1d1f21', 'important' ); 
      document.getElementById("prompt")
        .addEventListener( "keyup", function(event) {
        event.preventDefault();
        if ( event.keyCode === 13 ) {
          VFS_executeCmd( prompt.value );
        }
    });
      onLoad();
    });

    let prompt = null;
    let content = null;
    function onLoad() {
      prompt = document.getElementById( 'prompt' );
      content = document.getElementById( 'content' );

      VFS_init();
    }

    //[# Begin section #]
    //------------------------
    // API Functions
    //------------------------
    function VFS_init() {
      // Initialize current directory
      const v_VFSRoot = State_get( 'v_VFSRoot' );
      State_set( 'v_cwdObj', v_VFSRoot );
    }
    function VFS_executeCmd( userCmd ) {
      userCmd = userCmd.trim();
      let out = '';

      let funcName = '';
      let params = '';

      if( userCmd == 'cd..' ) {
        userCmd = 'cd ..';
      }

      // Get function name and params
      const idx = userCmd.indexOf( ' ' );
      if( idx != -1 ) {
        funcName = userCmd.substring( 0, idx );
        let paramContent = userCmd.substring( idx+1 );

        if( ( funcName == 'cd' ) && !paramContent.startsWith( "'" ) ) {
          paramContent = "'"+paramContent+"'";
        }
        params = '('+paramContent+')';
      } else {
        funcName = userCmd;
        params = '()';
      }

      // Compose VFS function name
      funcName = 'VFS_'+funcName;
      if( window[funcName] ) {
        // Compose function name
        const funCall = funcName+params;
        try {
          if( userCmd ) {
            // Execute function
            eval( `out = ${funCall};` );
          }
        } catch( e ) {
          out = 'Command not found';
        }
      } else {
        out = VFS_run( userCmd );
      }
      content.value = content.value+'\n'+
                      `# ${userCmd}\n`+
                      out.join( '\n' );
      content.scrollTop = content.scrollHeight;
      prompt.value = '';
      prompt.focus();
    }
    //[# End section #]

    function State_set( name, value ) {
      if( p[name] !== undefined ) {
        p[name] = value;
      } else if( t[name] !== undefined ) {
        t[name] = value;
      } else {
        console.error( `Error: field ${name} not found`)
      }
    }
    function State_get( name ) {
      if( p[name] !== undefined ) {
        return( p[name] );
      } else if( t[name] !== undefined ) {
        return( t[name] );
      } else {
        console.error( `Error: field ${name} not found`)
      }
    }
    //------------------------
    // API Functions
    //------------------------
    function VFS_run( command ) {
      const out = [];
      const v_cwdObj = State_get( 'v_cwdObj' );
      let isCommandFound = false;

      for( const entry in v_cwdObj ) {
        if( entry.startsWith( '__' ) ) {
          continue;
        }
        // If a directory starts with the itemName
        if( entry.startsWith( command ) && !entry.endsWith( '/' ) ) {
          const entryObj = v_cwdObj[entry];
          if( typeof( entryObj ) == 'function' ) {
            const result = entryObj();
            out.push(...result.split( '\n' ) );
            isCommandFound = true;
            break;
          }
          // // Complete the name
          // command = entry;
          // Compute function name
          // const funcName = v_cwdObj.__dirName+'_'+command;
          // if( window[funcName] ) {
          //   const result = window[funcName]();
          //   out.push(...result.split( '\n' ) );
          //   isCommandFound = true;
          //   break;
          // }
        }
      }
      if( !isCommandFound ) {
        out.push( 'Command not found' );
      }
      return( out );
    }
    function VFS_ls() {
      const out = [];
      const v_cwd = State_get( 'v_cwd' );
      const v_cwdObj = State_get( 'v_cwdObj' );

      out.push( `Content of: ${v_cwd}` );
      if( v_cwdObj.__prevDirObj ) {
        out.push( '../' );
      }
      for( const entry in v_cwdObj ) {
        if( entry.startsWith( '__' ) ) {
          continue;
        }
        if( typeof( v_cwdObj[entry] ) == 'object' ) {
          out.push( entry+'/' );
        } else {
          out.push( entry );
        }
      }
      return( out );
    }
    function VFS_cd( itemName ) {
      const out = [];
      let v_cwd = State_get( 'v_cwd' );
      const v_cwdObj = State_get( 'v_cwdObj' );

      let isDirFound = false;
      if( !itemName ) { // case of cd() => go to root
        // Go to root
        v_cwd = '/';
        State_set( 'v_cwd', v_cwd );
        const v_VFSRoot = State_get( 'v_VFSRoot' );
        State_set( 'v_cwdObj', v_VFSRoot );
        isDirFound = true;
      } else if( itemName == '..' ) {
        if( v_cwdObj.__prevDirObj ) {
          idx = v_cwd.slice( 0, -1 ).lastIndexOf( '/' );
          v_cwd = v_cwd.substring( 0, idx+1 );
          State_set( 'v_cwd', v_cwd );
          const newCWDObj = v_cwdObj.__prevDirObj;
          State_set( 'v_cwdObj', newCWDObj );
          isDirFound = true;
        }
      } else {
        for( const entry in v_cwdObj ) {
          if( entry.startsWith( '__' ) ) {
            continue;
          }
          // If a directory starts with the itemName
          if( entry.startsWith( itemName ) ) {
            const entryObj = v_cwdObj[entry];
            if( typeof( entryObj ) == 'object' ) { // If directory
              // Update current path
              v_cwd = v_cwd+entry+'/';

              // Store previous directory
              entryObj.__prevDirObj = v_cwdObj;
              // // Store current directory name
              // entryObj.__dirName = entry;
              // Update status
              State_set( 'v_cwd', v_cwd );
              State_set( 'v_cwdObj', entryObj );

              // Give feedback to user
              isDirFound = true;
              break;
            }
          }
        }
      }
      if( isDirFound ) {
        out.push( v_cwd );
      } else {
        out.push( `Directory "${itemName}" not found` );
      }
      return( out );
    }
    function VFS_pwd() {
      const out = [];
      const v_cwd = State_get( 'v_cwd' );
      out.push( v_cwd );
      return( out );
    }
  </script>
