  // Setup for diagram properties
  //[# Insert diagramSetup #]
}
function Diagram_setDSL( dsl ) {
}
//------------------------
// Internal Functions
//------------------------
function Diagram__getEmptyModel() {
  return( { nodeDataList: [], linkDataList: [] } );
}
function Diagram__setDiagramModel( model ) {
  if( model ) {
    State_set( 'd_model', model );
    // create the model data that will be represented by Nodes and Links
    for( const data of model.nodeDataArray ) {
      Diagram__addNode( data );
    }
  }
}
function Diagram__addNode( data ) {
  const d = State_get( 'd_diagram' );
  if( d ) {
    if( data ) {
      const nodeClass = {
        'label': draw2d.shape.basic.Label,
        'rectangle': draw2d.shape.basic.Rectangle,
        'circle': draw2d.shape.basic.Circle,
      };

      if( nodeClass[data.category] ) {
        const dataCopy = Object.assign( {}, data );
        data.userData = dataCopy;
        const node = new nodeClass[data.category]( data );
        d.add( node );
      }
    }
  }
}
