// Internal Functions
//------------------------
function Diagram__getNodeList() {
  let result = [];
  const model = State_get( 'd_model', Diagram__getEmptyModel() );
  if( model ) {
    // Return node list
    result = model.nodeDataArray;
  }
  return( result );
}
function Diagram__getLinkList() {
  let result = [];
  const model = State_get( 'd_model', Diagram__getEmptyModel() );
  if( model ) {
    // Return node list
    result = model.linkDataArray;
  }
  return( result );
}
