function Diagram_getSelectedDataList() {
  let result = [];
  const d = Diagram_getMainDiagram();
  if( d ) {
    const selection = d.getSelection();
    const sa = selection.getAll();
    const count = selection.getSize();
    for( let i = 0; i < count; ++i ) {
      const node = sa.get( i );
      const data = Diagram__getNodeData( node );
      if( data ) {
        result.push( data );
      }
    }
  }
  return( result );
}
function Diagram_getFirstSelectedData() {
  let result = null;
  const d = Diagram_getMainDiagram();
  if( d ) {
    const selection = d.getSelection();
    const sa = selection.getAll();
    const node = sa.first();
    if( node ) {
      result = Diagram__getNodeData( node );
    }
  }
  return( result );
}
function Diagram_getSelectionCount() {
  let result = 0;
  const d = Diagram_getMainDiagram();
  if( d ) {
    const selection = d.getSelection();
    const count = selection.getSize();
    result = count;
  }
  return( result );
}
//------------------------
// Internal Functions
//------------------------
function Diagram__getNodeData( node ) {
  let result = null;
  if( node ) {
    result = node.userData;
  }
  return( result );
}
function Diagram__getNodeCategory( node ) {
  let result = 'label';
  if( node ) {
    const map = {
      'draw2d_shape_basic_Label': 'label',
      'draw2d_shape_basic_Circle': 'circle',
    };
    if( map[node.cssClass] ) {
      result = map[node.cssClass];
    }
  }
  return( result );
}
