{"label":"WebApp","color":"lightsalmon","size":"213 89","category":"Hierarchy_CodeInGraph","isFile":true,"fileContent":"<!DOCTYPE html>
<html>
<head>
  <meta charset='utf-8'>
  <title>
   <!--[# Begin title #]-->
   Test WebApp
   <!--[# End title #]-->
  </title>

  <style>
  </style>


  <script>
    // WebApp Main Declarations
    const Root = {
      State: {
        Persistent: {  // Perstistent Data stored/loaded in User_state.json
          Diagram: {},
          Session: {},
        },
        Transient: {}, // Trainsient Data initialized/used in browser
      },
    };

    // WebbApp onLoad event
    window.addEventListener( 'load', ()=>{
      // Remove default margin
      window.document.body.style.margin = '0px';

      Root.mount( '/State/Persistent/Session', 'isReadOnly', true );
      Root.mount( '/State/Persistent/Session', 'currentGraphNode', {} );
      Root.mount( '/State/Persistent/Session', 'windowList', {} );
      Root.mount( '/State/Persistent/Session', 'openWindowList', {} );
      Root.mount( '/State/Transient/Diagram', 'instance', null );

      let text = 'Root = '+JSON.stringify( Root, null, 2 );
      appendText( text );

      Root.setState( '/State/Transient/Diagram/instance', 1 );
      text = 'Root = '+JSON.stringify( Root, null, 2 );
      appendText( text );
    });

    //------------------------
    // API Functions
    //------------------------
    Root.mount = function( path, name, obj ) {
      const cwd = Root.get( path );
      cwd[name] = obj;
    };
    Root.get = function( path ) {
      const keyList = path.split( '/' );
      let cwd = Root;
      keyList.forEach( (k)=> { if(k && cwd[k]) cwd = cwd[k] } );
      return( cwd );
    };
    Root.setState = function( name, value ) {
      if( name.startsWith( '/' ) ) {
        const [ path, pname ] = Root._getPathInfo( name );
        const cwd = Root.get( path );
        if( cwd ) {
          cwd[pname] = value;
        } else {
          console.error( `Error: field ${name} not found in state` );
        }
      } else if( Root.State.Persistent.Diagram[name] !== undefined ) {
        Root.State.Persistent.Diagram[name] = value;
      } else if( Root.State.Persistent.Session[name] !== undefined ){
        Root.State.Persistent.Session[name] = value;
      } else if( Root.State.Transient[name] !== undefined ){
        Root.State.Transient[name] = value;
      } else {
        console.error( `Error: field ${name} not found in state` );
      }
    };
    Root.getState = function( name ) {
      if( name.startsWith( '/' ) ) {
        const [ path, pname ] = Root._getPathInfo( name );
        const cwd = Root.get( path );
        if( cwd ) {
          return( cwd[pname] );
        } else {
          console.error( `Error: field ${name} not found in state` );
        }
      } else if( Root.State.Persistent.Diagram[name] !== undefined ) {
        return( Root.State.Persistent.Diagram[name] );
      } else if( Root.State.Persistent.Session[name] !== undefined ) {
        return( Root.State.Persistent.Session[name] );
      } else if( Root.State.Transient[name] !== undefined ) {
        return( Root.State.Transient[name] );
      } else {
        console.error( `Error: field ${name} not found in state` );
      }
    };
    Root._getPathInfo = function( path ) {
      const idx = path.lastIndexOf( '/' );
      const name = path.substring( idx+1 );
      path = path.substring( 0, idx );
      return( [ path, name ] );
    };
    function appendText( text ) {
      const el = document.createElement( 'pre' );
      el.innerText = text;
      document.body.appendChild( el );
    }
  </script>
