{"label":"WebApp","color":"lightsalmon","size":"213 84","category":"Hierarchy_CodeInGraph","isFile":true,"fileContent":"<!DOCTYPE html>
<html>
<head>
  <meta charset='utf-8'>
  <title>
   HyperGraph 3.1 - Diagram Test
  </title>

  <style>
    .mainDiagramDiv {
      position: absolute;
      /* top: -70px;   /* TODO: Remove this line, remove last lines */
      width: 100%;
      /* height: 120%; /* TODO: Restore to 100%
       remove also last lines in htmlMenu.js in function _computeRootMenuMouseClickXY */
      height: 100%;
      background: rgb(160, 160, 160);
    }
  </style>

  <!--GoJS Editor Includes-->
  <script type='text/javascript' src='lib/gojs/2.3.3/go.js'></script>
  <script type='text/javascript' src='lib/gojs/PolylineLinkingTool.js'></script>
  <script type='text/javascript' src='lib/gojs/SnapLinkReshapingTool.js'></script>
  <script type='text/javascript' src='lib/gojs/InGroupClickCreatingTool.js'></script>
  <!--End-->
  <!--WinBox Editor Includes-->
  <link rel="stylesheet" href="lib/winbox/0.2.73/modern.min.css">
  <link rel="stylesheet" href="lib/winbox/0.2.73/white.min.css">
  <script type="text/javascript" src="lib/winbox/0.2.73/winbox.bundle.min.js"></script>
  <!--End-->

  <script>
    // WebApp Main Declarations
    var $ = go.GraphObject.make;  // for conciseness in defining templates
    var root = {
      p: {},  // Perstistent Data
      t: {},  // Trainsient Data
    };
    const Diagram = {};
    root.t.window = null;
    const Window = {};

    // WebbApp onLoad event
    window.addEventListener( 'load', ()=>{
      // Remove default margin
      window.document.body.style.margin = '0px';

      // Define diagram class
      root.t.mainDiagram = Diagram._new( 'mainDiagram' );
      // define a simple Node template (but use the default Link template)
      Diagram.addDSL( $(go.Node, "Auto",
        //{ contextMenu: myContextMenu },
        $(go.Shape, "RoundedRectangle",
          {
            doubleClick: function( e, node ) { Window.openNode( node ); },
          },

          // Shape.fill is bound to Node.data.color
          new go.Binding("fill", "color")
        ),
        $(go.TextBlock,
          { 
            margin: 3,
            doubleClick: function( e, node ) { Window.openNode( node ); },
          },  // some room around the text

          // TextBlock.text is bound to Node.data.key
          new go.Binding("text", "key")
        )
      ));
      //[# End exex #]
      // create the model data that will be represented by Nodes and Links
      Diagram.setModel( new go.GraphLinksModel(
        [
          {
            "key": "Alpha",
            "color": "#f38181",
            "isFile": true,
            "fileType": "application/html",
            "fileURL": "https://en.wikipedia.org/wiki/Alpha"
          },
          { 
            "key": "Beta",
            "color": "PaleGreen",
            "isFile": true,
            "fileType": "application/html",
            "fileURL": "https://en.wikipedia.org/wiki/Beta"
          },
          {
            "key": "Gamma",
            "color": "#95e1d3",
            "isFile": true,
            "fileType": "application/html",
            "fileURL": "https://en.wikipedia.org/wiki/Gamma"
          },
          { "key": "Delta", "color": "#fce38a" }
        ],
        [
          { "from": "Alpha", "to": "Beta" },
          { "from": "Alpha", "to": "Gamma" },
          { "from": "Beta",  "to": "Beta" },
          { "from": "Gamma", "to": "Delta" },
          { "from": "Delta", "to": "Alpha" }
        ]
      ));
    });

    Diagram._new = function( divId ) {
      const result =  $(go.Diagram, divId,  // create a Diagram for the DIV HTML element
      {
        "undoManager.isEnabled": true,
      });
      return( result );
    };
    Diagram.addDSL = function ( dsl ) {
      const md = root.t.mainDiagram;
      md.nodeTemplate = dsl;
    };
    Diagram.setModel = function( model ) {
      const md = root.t.mainDiagram;
      md.model = model;
    };
    Window.openNode = function( node ) {
      if( node ) {
        const data = node.part.data;
        if( data.fileURL ) {
          const winInfo = { 
            title: data.label, 
            background: data.color, 
            class: 'modern',
            url: data.fileURL,
            x: 10,  y: 10,  width: 250, height: 150,
          };
          const win = new WinBox( winInfo );
        } else if( data.fileContent ) {

        }
      }
    };
  </script>
</head>
<body>
  <div id="mainDiagram" class="mainDiagramDiv"></div>
  <div id='mainWindowDiv'></div>
</body>
</html>","fileTypeName":"Web Page","fileType":"application/html","key":497,"location":"9088.978 12567.5","group":498,"editorPosition":[3220,2338,341,279]},
