{"label":"Engine Base API 1.1","color":"tan","size":"286 111","category":"Hierarchy_CodeInGraph","isFile":true,"fileContent":"//------------------------------
// Engine Base API
//------------------------------
function Engine_get( data, name, defaultValue ) {
  // Get node instance
  const ni = Engine_getNodeInstance( data );

  // Set default value if property did not exist
  if( ni.property[name] === undefined ) {
    ni.property[name] = defaultValue;
  }
  // Return property value
  return( ni.property[name] );
}
function Engine_set( data, name, value ) {
  // Get node instance
  const ni = Engine_getNodeInstance( data );

  // Set property value
  ni.property[name] = value;
}
function Engine_getInput( data, name, defaultValue ) {
  // Get node instance
  const ni = Engine_getNodeInstance( data );

  // Update default value
  if( ni.input[name] == undefined ) {
    //ni.input[name] = defaultValue;
    return( defaultValue );
  }
  return( ni.input[name] );
  //return( ni.input[name].value );
}
function Engine_setInput( data, name, value ) {
  // Get node instance
  const ni = Engine_getNodeInstance( data );

  // Update value
  ni.input[name] = value;
  //ni.input[name] = { value };
  // Update state
  if( !ni.inputState[name] ) {
    ni.inputState[name] = {};
  }
  ni.inputState[name].isNew = true;
}
function Engine_setInputDefault( data, defaultValue ) {
  // Get node instance
  const ni = Engine_getNodeInstance( data );
  // Get list of input names
  const inNameList = Engine_getInputNameList( data );
  for( const inName of inNameList ) {
    if( ni.input[inName] == undefined ) {
      ni.input[inName] = defaultValue;
      //ni.input[inName] = { value: defaultValue };
    }
  }
}
function Engine_getInputValueMap( data, defaultValue ) {
  const result = {};
  // Get node instance
  const ni = Engine_getNodeInstance( data );
  // Get list of input names
  const inNameList = Engine_getInputNameList( data );
  for( const inName of inNameList ) {
    const value = ni.input[inName];
    //const valueRef = ni.input[inName];
    result[inName] = ( value? value: defaultValue );
    //result[inName] = ( valueRef? valueRef.value: defaultValue );
  }
  return( result );
}
function Engine_fireOutput( data, name, value ) {
  // Fire only connected outputs
  if( Engine_isOutputConnected( data, name ) ) {
    // Get node instance
    const ni = Engine_getNodeInstance( data );

    // Save output
    ni.output[name] = value;
    //ni.output[name] = { value };
  }
}
function Engine_getLanguage( data ) {
  let result = 'text';
  if( data.fileType ) {
    const [format, language] = data.fileType.split( '/' );
    result = language;
  }
  return( result );
}","fileTypeName":"Javascript","fileType":"text/javascript","key":441,"location":"2850 -630","isIncludeScript":true,"editorPosition":[4167,914,586,1571]},
