{"label":"Engine Base Library 1.1","color":"tan","size":"286 111","category":"Hierarchy_CodeInGraph","isFile":true,"fileContent":"//------------------------------
// Engine Base Library
//------------------------------

// Initialize Engine
if( !graphData.nodeInstance ) {
  Engine_clearNodeInstance();
}

//------------------------------
// Engine Base Library
//------------------------------
function Engine_getNodeInstance( data ) {
  // If node not existing => create it
  if( !graphData.nodeInstance[data.key] ) {
    graphData.nodeInstance[data.key] = {
      property: {},
      input: {},
      inputState: {},
      output: {},
      isCompute: false,
    };
  }
  // Return node instance
  return( graphData.nodeInstance[data.key] );
}
function Engine_clearNodeInstance() {
  // Persistent link data for nodes
  graphData.nodeInstance = {};
  graphData.engineInstance = {
    currNodeSelectionKey: -1, // Animation
  };
}
function Engine_isNodeEnabled( data ) {
  let result = true;
  if( data.isGroup ) {
    result = ( ( data.color == undefined ) || ( data.color.toLowerCase() == 'green' ) );
  } else if( data.group !== undefined ) {
    const gData = graphData.me.getNode( graphData.me.modelId, data.group );
    result = ( gData && ( gData.color.toLowerCase() == 'green' ) );
  }
  return( result );
}
function Engine_getInputLinkDataList( data, name ) {
  const result = [];
  const pushInDataList = ()=> {
    // Get input link from port name
    const inDataList = graphData.me.getLinkListFanInByNodeKey( graphData.modelId, 
                                                     data.key,
                                                     name );
    for( const inData of inDataList ) {
      if( Engine_isNodeEnabled( inData ) ) {
        result.push( inData );
      }
    }
  };

  // Check if name exist
  if( data.in_ ) {
    for( const inInfo of data.in_ ) {
      if( inInfo.portId == name ) {
        pushInDataList();
        break;
      }
    }
  } else {
    name = undefined;  // No input name in this case
    pushInDataList();
  }
  return( result );
}
function Engine_getOutputLinkDataList( data, name ) {
  const result = [];
  const pushOutDataList = ()=> {
    // Get output link from port name
    const outDataList = graphData.me.getLinkListFanOutByNodeKey( graphData.modelId,
                                                    data.key,
                                                    name );
    for( const outData of outDataList ) {
      if( Engine_isNodeEnabled( outData ) ) {
        result.push( outData );
      }
    }
  };

  // Check if name exist
  if( data.out_ ) {
    for( const outInfo of data.out_ ) {
      if( outInfo.portId == name ) {
        pushOutDataList();
        break;
      }
    } 
  } else {
    name = undefined; // No input name in this case
    pushOutDataList();
  }
  return( result );
}
function Engine_fetchInputs( data ) {
  const storeValue = ( data, fromInName, toInName, defaultValue )=> {
  //const storeValue = ( data, fromInName, toInName )=> {
    const inLinkList = graphData.me.getLinkListFanInByNodeKey( graphData.modelId,
                                                            data.key,
                                                            fromInName );
    for( const inLink of inLinkList ) {
      // Get source node
      const outData = graphData.me.getNode( graphData.me.modelId, inLink.from );

      if( Engine_isNodeEnabled( outData ) ) {
        const value = Engine_getOutput( outData, inLink.fromPort, defaultValue );
        Engine_setInput( data, toInName, value );
        // const valueRef = Engine_getOutputRef( outData, inLink.fromPort );
        // Engine_setInputRef( data, toInName, valueRef );
        break;
      }
    }
  }
  // Loop over all connected inputs
  const inNameList = Engine_getInputNameList( data, true );
  // Case of component with input ports
  if( inNameList.length ) {
    for( const inName of inNameList ) {
      // Store in input inName the first enabled link value comming into it
      storeValue( data, inName, inName, null );
      //storeValue( data, inName, inName );
    }
  } else {
    // Case of component without input ports
    // Store in input '*' the first enabled link value comming into it
    storeValue( data, undefined, '*', undefined );
    //storeValue( data, undefined, '*' );
  }
}
function Engine_isInput( data, name ) {
  let result = false;
  if( data.in_ ) {
    data.in_.find( (i)=> { if( i.portId == name ) result = true } );
  }
  return( result )
}
function Engine_isOutput( data, name ) {
  let result = false;
  if( data.out_ ) {
    data.out_.find( (o)=> { if( o.portId == name ) result = true } );
  }
  return( result )
}
function Engine_isInputConnected( data, name ) {
  // Get input link list
  const list = Engine_getInputLinkDataList( data, name );
  // If input is connected
  return( list && list.length )
}
function Engine_isOutputConnected( data, name ) {
  // Get output link list
  const list = Engine_getOutputLinkDataList( data, name );
  // If output is connected
  return( list && list.length )
}
function Engine_getInputNameList( data, isOnlyConnected ) {
  const result = []
  if( data.in_ ) {
    for( const inInfo of data.in_ ) {
      const name = inInfo.portId;
      // If we want only connected one => skip unconnected one
      if( ( isOnlyConnected != undefined ) && isOnlyConnected ) {
        if( !Engine_isInputConnected( data, name ) ) {
          continue;
        }
      }
      result.push( name );
    }
  }
  return( result );
}
function Engine_getOutputNameList( data, isOnlyConnected ) {
  const result = []
  if( data.out_ ) {
    for( const outInfo of data.out_ ) {
      const name = outInfo.portId;
      // If we want only connected one => skip unconnected one
      if( ( isOnlyConnected != undefined ) && isOnlyConnected ) {
        if( !Engine_isOutputConnected( data, name ) ) {
          continue;
        }
      }
      result.push( name );
    }
  }
  return( result );
}","fileTypeName":"Javascript","fileType":"text/javascript","key":461,"location":"2850 -910","isIncludeScript":true,"editorPosition":[1385,113,772,2993]},
