  <button onclick="Window_openXY( this, 0 )">Node0(x,y)</button>
  <button onclick="Window_openXY( this, 1 )">Node1(x,y)</button><br><br>
  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
  <button onclick="Window_open( this, 2 )">Node2()</button>
  <button onclick="Window_open( this, 3 )">Node3()</button><br><br>
