{"label":"Window","category":"CodeFlow_Code","size":"180 100","color":"orange","props_":[],"in_":[],"out_":[{"portId":"declaration","name":"declaration"},{"portId":"onload","name":"onload"},{"portId":"function","name":"function"}],"isFile":true,"fileTypeName":"Javascript","fileType":"text/javascript","key":429,"location":"4550 15191.25","fileContent":"//[# Begin declaration #]
const Window = {};
//[# End declaration #]

//[# Begin onload #]
// Define window class
Window._init( 'windowDiv' );
//[# End onload #]

//[# Begin function #]
/**
 *  Window read-only properties:
 *   elId:     id of the element containing the Window
 *   el:       HTML element element containing the Window
 */
Window._init = function( elId ) {
  const el = document.getElementById( elId );
  if( el ) {
    const self = Object.assign( {}, Window );

    self.elId = elId;
    self.el = el;

    // w_openWinList: {
    //   ...
    //   graphURL-n: {
    //    key1: { isPin: true },
    //    key2: { isPin: false },
    //     ...
    //   }
    // }
    Root.mount( '/State/Persistent/Session/', 'w_openWinList', {} );

    // w_positionWinList: {
    //   ...
    //   graphURL-n: {
    //     key1: [x, y, width, height],
    //     key2: [x, y, width, height],
    //     ...
    //   },
    // }
    Root.mount( '/State/Persistent/Session/', 'w_positionWinList', {} );

    // w_winList: {
    //   ...
    //   graphURL-n: {
    //     key1: win,
    //     key2: win, 
    //     ...
    //   }
    // }
    Root.mount( '/State/Transient/', 'w_winList', {} );

    // In /State/Transient/ContainerList: {
    //     ...
    //     graphURL-n: {
    //       windowId1: win,
    //       windowId2: win
    //       ...
    //     }
  }
}
Window.alert = function( msg, isCenter ) {
  isCenter = ( isCenter == undefined? true: isCenter );
  new WinBox( 'Alert', {
    modal: true,
    autosize: true,
    background: 'Crimson',
    html: `<div style="margine: 0px;">`+
            `<pre style="${isCenter? 'text-align: center;':''}">`+
              msg+
            `</pre>`+
          `</div>`,
  });
}
// Override standard alert function
alert = Window.alert;
Window.openWindow = function( nData, onLoaded, x, y ) {
  // If nData is defined and can't put win on focus => open new
  if( nData && !Window._tryFocusWindow( nData ) ) {
    // Get window position (previous or default)
    const winLoc = Window._getLocation( nData );
    // Initialize coordinates
    if( x != undefined ) {
      winLoc[0] = x;
    }
    if( y != undefined ) {
      winLoc[1] = y;
    }

    // Create window info
    const winInfo = Window._createWinInfo( nData, winLoc );
    // Open a new window
    const win = new WinBox( winInfo );
    const windowId = System.getNDataUniqueId( nData );
    Root.mount( '/State/Transient/ContainerList', windowId, win );

    // Customize Window
    Window._customizeWindow( win, { key: nData.key } );

    // Store window position and status
    Window._setLocation( nData, winLoc );
    Window._setOpenWindow( nData, win );
  }
}
Window.tooglePin = function( nData ) {
  if( nData ) {
    const dURL = System.graphURL();
    const ol = Root.getState( 'w_openWinList' );
    if( !ol[dURL] ) {
      ol[dURL] = {}; 
    }
    const key = nData.key;
    ol[dURL][key].isPin = !ol[dURL][key].isPin;
  }
}
Window.isPin = function( nData ) {
  let result = false;
  if( nData ) {
    const dURL = System.graphURL();
    const key = nData.key;
    const ol = Root.getState( 'w_openWinList' );
    if( ol[dURL] && ol[dURL][key] ) {
      result = ol[dURL][key].isPin;
    }
  }
  return( result );
}
Window.getWindow = function( nData ) {
  // TODO: return the window managing the nData
  //       It should be possible to apply
  //       w = Window_getWindow( nData )
  //       src = Window_getSource( w )
}
Window.closeUnpinWindow = function() {
  const dURL = System.graphURL();
  const wl = Root.getState( 'w_winList' );
  const winList = wl[dURL];
  if( winList ) {
    for( const key in winList ) {
      const win = winList[key];
      if( win ) {
        const nData = { key };
        if( !Window_isPin( nData ) ) {
          win.close();
        }
      }
    }
  }
}
Window.closeAllWindow = function() {
  const dURL = System.graphURL();
  const wl = Root.getState( 'w_winList' );
  const winList = wl[dURL];
  if( winList ) {
    for( const key in winList ) {
      const win = winList[key];
      if( win ) {
        win.close();
      }
    }
  }
}
//[# End function #]","editorPosition":[261,412,637,2585],"group":507},
