<!DOCTYPE html>
<html>
<head>
  <meta charset='utf-8'>
  <title>
   <!--[# Begin title #]-->
   Test WebApp
   <!--[# End title #]-->
  </title>

  <style>
    /*[# Insert style #]*/
    body {
      background-color: black;
      margine: 0px;
    }
  </style>

  <!--[# Insert include #]-->

  <script>
    // WebApp Main Declarations
    //[# Insert declaration #]

    // WebbApp onLoad event
    window.addEventListener( 'load', ()=>{
      // Remove default margin
      window.document.body.style.margin = '0px';

      //[# Insert onload #]
    });

    //[# Insert function #]
    setEditorChanged = function() {
      if( !Status.isReadOnly() ) {
        Status.setNeedSave();
        setTimeout( Status.setSaved.bind(Status), 1*1000 );
      }
    }
  </script>
</head>
<body>
  <!--[# Insert body #]-->
  <br><br>
  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
  <button onclick="setEditorChanged()">Simulate EditorChanged</button><br>
  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
  <button onclick="Status.setLoading()">Simulate EditorLoading</button><br>
  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
  <button onclick="Status.setError()">Simulate Error Status</button><br>
  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
  <button onclick="Status.setReady()">Simulate Ready Status</button><br>
  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
  <button onclick="Status.toogleReadOnly()">Simulate Toogle ReadOnly</button><br>
</body>
