    const dURL = System.graphURL();
    const pl = Root.getState( 'w_positionWinList' );
    let winList = pl[dURL];
    if( winList ) {
      const key = nData.key;
      const winPos = winList[key];
      if( winPos ) {
        result = winPos;
      }
    }
  }
  return( result );
}
Window._setLocation = function( nData, winPos ) {
  if( nData ) {
    const dURL = System.graphURL();
    const pl = Root.getState( 'w_positionWinList' );
    if( !pl[dURL] ) {
      pl[dURL] = {};
    }
    const key = nData.key;
    pl[dURL][key] = winPos;
  }
}
Window._setPosition = function( nData, x, y ) {
  if( nData ) {
    const dURL = System.graphURL();
    const pl = Root.getState( 'w_positionWinList' );
    if( !pl[dURL] ) {
      pl[dURL] = {};
    }
    const key = nData.key;
    if( pl[dURL][key] ) {
      pl[dURL][key][0] = x;
      pl[dURL][key][1] = y;
    } else {
      pl[dURL][key] = [x, y, 250, 200];
    }
  }
}
Window._setSize = function( nData, w, h ) {
  if( nData ) {
    const dURL = System.graphURL();
    const pl = Root.getState( 'w_positionWinList' );
    if( !pl[dURL] ) {
      pl[dURL] = {};
    }
    const key = nData.key;
    if( pl[dURL][key] ) {
      pl[dURL][key][2] = w;
      pl[dURL][key][3] = h;
    } else {
      pl[dURL][key] = [10, 10, w, h];
    }
  }
}
Window._setOpenWindow = function( nData, win ) {
  if( nData ) {
    const dURL = System.graphURL();
    const ol = Root.getState( 'w_openWinList' );
    if( !ol[dURL] ) {
      ol[dURL] = {}; 
    }
    const key = nData.key;
    ol[dURL][key] = { isPin: false };

    const wl = Root.getState( 'w_winList' );
    if( !wl[dURL] ) {
      wl[dURL] = {}; 
    }
    wl[dURL][key] = win;
  }
}
Window._tryFocusWindow = function( nData ) {
  let result = false;
  if( nData ) {
    const dURL = System.graphURL();
    const wl = Root.getState( 'w_winList' );
    const winList = wl[dURL];
    if( winList ) {
      const key = nData.key;
      const win = winList[key];
      if( win ) {
        win.focus( true );
        result = true;
      }
    }
  }
  return( result );
}
Window._createWinInfo = function( nData, winLoc ) {
  // Set window property
  const title = System.getNDataTitle( nData );
  const wClass = 'modern'; // class is a reserved keyword
  const background = nData.color || 'Plum';

  // Window info
  const winInfo = { 
    title, background, class: wClass,
    x: winLoc[0],     y: winLoc[1],
    width: winLoc[2], height: winLoc[3],
    onmove: (x,y)=>{
      Window._setPosition( nData, x, y );
    },
    onresize: (w,h)=>{
      Window._setSize( nData, w, h );
    },
    onclose: (e)=>{
      Window._removeWindow( nData );
    },
  };

  if( nData.fileURL ) {
    winInfo.url = nData.fileURL;
  }

  return( winInfo );
}
Window._customizeWindow = function( win, nDataKey ) {
  win.removeControl("wb-max");
  win.addControl({
      index: 2,
      class: "wb-like",
      image: "/fileServer/pictures/unpinnedw.svg",
      click: function( event, winbox ) {
          // the winbox instance will be passed as 2nd parameter
          // console.log(winbox.id);
          // "this" refers to the button which was clicked:
          this.classList.toggle( "active" );
          Window.tooglePin( nDataKey );
      }
  });
}
Window._removeWindow = function( nData ) {
  if( nData ) {
    const dURL = System.graphURL();
    // Remove from winList
    const wl = Root.getState( 'w_winList' );
    const winList = wl[dURL];
    if( winList ) {
      const key = nData.key;
      if( winList[key] ) {
        delete winList[key];
      }
    }
    // Remove from openList
    const ol = Root.getState( 'w_openWinList' );
    const openList = ol[dURL];
    if( openList ) {
      const key = nData.key;
      if( openList[key] ) {
        delete openList[key];
      }
    }
  }
}
