{"label":"WebApp","color":"lightsalmon","size":"213 84","category":"Hierarchy_CodeInGraph","isFile":true,"fileContent":"<!DOCTYPE html>
<html>
<head>
  <meta charset='utf-8'>
  <title>
   <!--[# Begin title #]-->
   Test WebApp
   <!--[# End title #]-->
  </title>

  <style>
    #statusDiv {
      position: absolute;
      width: 100%;
      height: 100%;
      box-sizing: border-box;
      border: 10px;
      border-style: solid;
      border-color: rgba(125, 125, 125, 0.6);
      z-index: 100;
      pointer-events: none;
    }
    #statusDiv.loading {
      border-color: rgba(30, 144, 255, 0.6);
    }
    #statusDiv.default {
      border-color: rgba(125, 125, 125, 0.6);
    }
     #statusDiv.error {
      border-color: rgba(255, 0, 0, 0.6);
    }
    #statusDiv.warning {
      border-color: rgba(255, 221, 0, 0.6);
    }
    #statusDiv.saved {
      animation-name: saved;
      animation-duration: 2s;
    }
    @keyframes saved {
      from { border-color: rgba(123, 255, 0, 0.6); }
      to { border-color: rgba(125, 125, 125, 0.6); }
    }
    .mainGraphDiv {
      position: absolute;
      /* top: -70px;   /* TODO: Remove this line, remove last lines */
      width: 100%;
      /* height: 120%; /* TODO: Restore to 100%
       remove also last lines in htmlMenu.js in function _computeRootMenuMouseClickXY */
      height: 100%;
      background: rgb(160, 160, 160);
    }
    .wb-like {
        background-size: 20px auto;
    }
    .wb-like.active {
        background-image: url(/fileServer/pictures/pinnedw.svg) !important;
    }
  </style>

  <!--GoJS Editor Includes-->
  <script type='text/javascript' src='lib/gojs/2.3.3/go.js'></script>
  <!--End-->
  <!--WinBox Editor Includes-->
  <link rel="stylesheet" href="lib/winbox/0.2.73/modern.min.css">
  <link rel="stylesheet" href="lib/winbox/0.2.73/white.min.css">
  <script type="text/javascript" src="lib/winbox/0.2.73/winbox.bundle.min.js"></script>
  <!--End-->
  <!--HTMLMenu includes-->
  <script type='text/javascript' src='lib/htmlMenu/1.2/htmlMenu.js'></script>
  <link type='text/css' rel='stylesheet' href='lib/htmlMenu/1.2/htmlMenu.css'>
  <!--End-->

  <script>
    // WebApp Main Declarations
    const Root = {
      State: {
        Persistent: {  // Perstistent Data stored/loaded in User_state.json
          Diagram: {},
          Session: {},
        },
        Transient: {  // Trainsient Data initialized/used in browser
          ContainerList: {},
        },
      },
    };
    const Status = {};
    var $ = go.GraphObject.make;  // for conciseness in defining templates
    const Graph = {};
    let MainGraph = null;  // Shortcut to the Main Graph
    const System = {};
    const Window = {};
    const ContextMenu = {};

    // WebbApp onLoad event
    window.addEventListener( 'load', ()=>{
      // Remove default margin
      window.document.body.style.margin = '0px';

      Status._init( 'statusDiv' );
      // Define main Graph class
      MainGraph = Graph._new( 'mainGraph' );
      // Define system class
      System._init();
      // Define window class
      Window._init( 'windowDiv' );

      // Create a context menu
      const cm = ContextMenu._new( 'contextMenuDiv' );

      // Initialize menu context params
      cm.contextMenu.addParams( 'gojs', {
        diagram: MainGraph.diagram,
      } );
      // Create context menu items
      cm.contextMenu.add({
        diagramMenu: {	layout: 'vertical', itemList: [
                					{ label: 'Properties', do: ( o )=> alert( 'Property selected' ) },
                					{ label: 'View',       do: ( o )=> alert( 'View Selected' ) },
                					{ label: 'Tools',      do: ( o )=> alert( 'Tools Selected' ) },
                				],
                     },
      });
      // Connect context menu to diagram
      MainGraph.diagram.contextMenu = cm.contextMenu.getMenu( 'diagramMenu' );
    });

    //------------------------
    // API Functions
    //------------------------
    Root.mount = function( path, name, obj ) {
      const cwd = Root.get( path );
      cwd[name] = obj;
    };
    Root.get = function( path ) {
      const keyList = path.split( '/' );
      let cwd = Root;
      keyList.forEach( (k)=> { if(k && cwd[k]) cwd = cwd[k] } );
      return( cwd );
    };
    Root.setState = function( name, value ) {
      if( name.startsWith( '/' ) ) {
        const [ path, pname ] = Root._getPathInfo( name );
        const cwd = Root.get( path );
        if( cwd ) {
          cwd[pname] = value;
        } else {
          console.error( `Error: field ${name} not found in state` );
        }
      } else if( Root.State.Persistent.Diagram[name] !== undefined ) {
        Root.State.Persistent.Diagram[name] = value;
      } else if( Root.State.Persistent.Session[name] !== undefined ){
        Root.State.Persistent.Session[name] = value;
      } else if( Root.State.Transient[name] !== undefined ){
        Root.State.Transient[name] = value;
      } else {
        console.error( `Error: field ${name} not found in state` );
      }
    };
    Root.getState = function( name ) {
      if( name.startsWith( '/' ) ) {
        const [ path, pname ] = Root._getPathInfo( name );
        const cwd = Root.get( path );
        if( cwd ) {
          return( cwd[pname] );
        } else {
          console.error( `Error: field ${name} not found in state` );
        }
      } else if( Root.State.Persistent.Diagram[name] !== undefined ) {
        return( Root.State.Persistent.Diagram[name] );
      } else if( Root.State.Persistent.Session[name] !== undefined ) {
        return( Root.State.Persistent.Session[name] );
      } else if( Root.State.Transient[name] !== undefined ) {
        return( Root.State.Transient[name] );
      } else {
        console.error( `Error: field ${name} not found in state` );
      }
    };
    Root._getPathInfo = function( path ) {
      const idx = path.lastIndexOf( '/' );
      const name = path.substring( idx+1 );
      path = path.substring( 0, idx );
      return( [ path, name ] );
    };
    //------------------------
    // API Functions
    //------------------------
    /**
     *  Status read-only properties:
     *   elId:    id of the element containing the status
     *   el:      HTML element element containing the status
     */
    Status._init = function( elId ) {
      const el = document.getElementById( elId );
      if( el ) {
        Root.mount( '/State/Persistent/Session', 's_isReadOnly', false );
        Root.mount( '/State/Persistent/Session', 's_statusName', true );

        Root.mount( '/State/Transient/', 's_status', Status );
        Status.elId = elId;
        Status.el = el;
      } else {
        console.error( `Error: div "${elId}" not found` );
      }
    }
    Status.toogleReadOnly = function() {
      const ro = !Root.getState( 's_isReadOnly' );
      Root.setState( 's_isReadOnly', ro );

      this.el.style['border-style'] = ( ro? 'dashed': 'solid' );
      Root.setState( 's_statusName', ( ro? 'readOnly': 'read&Write' ) );
    }
    Status.s_isReadOnly = function() {
      return( Root.getState( 's_isReadOnly' ) );
    }
    Status.setError = function() {
      this.el.className = 'error';
      Root.setState( 's_statusName', 'error' );
    }
    Status.setReady = function() {
      this.el.className = 'default';
      Root.setState( 's_statusName', 'ready' );
    }
    Status.setLoading = function() {
      this.el.className = 'loading';
      Root.setState( 's_statusName', 'loading' );
    }
    Status.setNeedSave = function() {
      if( !Status.s_isReadOnly() ) {
        this.el.className = 'warning';
        Root.setState( 's_statusName', 'warning' );
      }
    }
    Status.setSaved = function() {
      if( !Status.s_isReadOnly() ) {
        this.el.className = 'saved';
        Root.setState( 's_statusName', 'ready' );
      }
    }
    Status.getStatusName = function() {
      return( Root.getStatus( 's_statusName' ) );
    }
    /**
     *  MainGraph read-only properties:
     *   elId:    id of the element containing the Graph
     *   el:      HTML element element containing the Graph
     *   nData:   nData of the current Graph
     *   url:     nData fileURL
     *   diagram: diagram canvas object
     *   title:   Graph title
     */
    Graph._new = function( elId ) {
      let self = null;

      const el = document.getElementById( elId );
      if( el ) {
        self = Object.assign( {}, Graph );
        Root.mount( '/State/Transient/ContainerList', `d_${elId}`, self );

        const diagram =  $(go.Diagram, elId,  // create a diagram for the DIV HTML element
        {
          "undoManager.isEnabled": true,
        });

        // Read-only Graph property
        self.elId = elId;
        self.el = el;
        self.nData = null;
        self.url = 'emptyURL';
        self.diagram = diagram;
        self.title = '';
      } else {
        console.error( `Error: div "${elId}" not found` );
      }
      return( self );
    }
    Graph.getNData = function() {
      return( this.nData );
    }
    Graph.setNData = function( nData ) {
      let result = false;

      if( nData.isDir && nData.fileURL && nData.fileContent ) {
        this.nData = nData;
        try {
          const jsonModel = JSON.parse( nData.fileContent );
          const model = go.Model.fromJson( jsonModel );
          this.setModel( model );
          this.nData = nData;
          this.url = nData.fileURL;
          this.title = ( nData.label? nData.label: `${nData.fileURL}:${nData.key}` );
          result = true;
        } catch( e ) {
          console.warn( 'Graph could not set nData', nData );
        }
      }
      return( result );
    }
    Graph.addDSL = function ( dsl ) {
      this.diagram.nodeTemplate = dsl;
    }
    Graph.setModel = function( model ) {
      this.diagram.model = model;
    }
    Graph._getEmptyModel = function() {
      return( new go.GraphLinksModel( [], [] ) );
    }
    /**
     *  System read-only properties:
     */
    System._init = function( ) {
      Root.mount( '/State/Transient/', 'y_system', System );
    };
    System.dateTime = function() {
      new Date().toLocaleString();
    }
    System.date = function() {
      return( new Date().toLocaleDateString() );
    }
    System.time = function() {
      return( new Date().toLocaleTimeString() );
    }
    System.graphPath = function() {
      return( MainGraph.url );
    }
    System.graphURL = function() {
      return( MainGraph.url );
    }
    System.getNDataTitle = function( nData ) {
      let result = 'noName';

      if( nData ) {
        if( nData.label ) {
          result = nData.label;
        } else if( nData.fileURL && nData.key ) {
          result = `${nData.fileURL}:${nData.key}`;
        } else if( nData.key ) {
          result = nData.key;
        }
      }
      return( result );
    }
    System.getNDataUniqueId = function( nData ) {
      let result = 'noId';

      if( nData ) {
        const url = ( nData.fileURL? nData.fileURL: 'emptyURL' );
        const id = `${url}_${nData.key}`;
        return( id.replaceAll( /[^A-Za-z0-9]/g, "_" ) ); // make valid identifier
      }
      return( result );
    }
    /**
     *  Window read-only properties:
     *   elId:     id of the element containing the Window
     *   el:       HTML element element containing the Window
     */
    Window._init = function( elId ) {
      const el = document.getElementById( elId );
      if( el ) {
        const self = Object.assign( {}, Window );

        self.elId = elId;
        self.el = el;

        // w_openWinList: {
        //   ...
        //   graphURL-n: {
        //    key1: { isPin: true },
        //    key2: { isPin: false },
        //     ...
        //   }
        // }
        Root.mount( '/State/Persistent/Session/', 'w_openWinList', {} );

        // w_positionWinList: {
        //   ...
        //   graphURL-n: {
        //     key1: [x, y, width, height],
        //     key2: [x, y, width, height],
        //     ...
        //   },
        // }
        Root.mount( '/State/Persistent/Session/', 'w_positionWinList', {} );

        // w_winList: {
        //   ...
        //   graphURL-n: {
        //     key1: win,
        //     key2: win, 
        //     ...
        //   }
        // }
        Root.mount( '/State/Transient/', 'w_winList', {} );

        // In /State/Transient/ContainerList: {
        //     ...
        //     graphURL-n: {
        //       windowId1: win,
        //       windowId2: win
        //       ...
        //     }
      }
    }
    Window.alert = function( msg, isCenter ) {
      isCenter = ( isCenter == undefined? true: isCenter );
      new WinBox( 'Alert', {
        modal: true,
        autosize: true,
        background: 'Crimson',
        html: `<div style="margine: 0px;">`+
                `<pre style="${isCenter? 'text-align: center;':''}">`+
                  msg+
                `</pre>`+
              `</div>`,
      });
    }
    // Override standard alert function
    alert = Window.alert;
    Window.openWindow = function( nData, onLoaded, x, y ) {
      // If nData is defined and can't put win on focus => open new
      if( nData && !Window._tryFocusWindow( nData ) ) {
        // Get window position (previous or default)
        const winLoc = Window._getLocation( nData );
        // Initialize coordinates
        if( x != undefined ) {
          winLoc[0] = x;
        }
        if( y != undefined ) {
          winLoc[1] = y;
        }

        // Create window info
        const winInfo = Window._createWinInfo( nData, winLoc );
        // Open a new window
        const win = new WinBox( winInfo );
        const windowId = System.getNDataUniqueId( nData );
        Root.mount( '/State/Transient/ContainerList', windowId, win );

        // Customize Window
        Window._customizeWindow( win, { key: nData.key } );

        // Store window position and status
        Window._setLocation( nData, winLoc );
        Window._setOpenWindow( nData, win );
      }
    }
    Window.tooglePin = function( nData ) {
      if( nData ) {
        const dURL = System.graphURL();
        const ol = Root.getState( 'w_openWinList' );
        if( !ol[dURL] ) {
          ol[dURL] = {}; 
        }
        const key = nData.key;
        ol[dURL][key].isPin = !ol[dURL][key].isPin;
      }
    }
    Window.isPin = function( nData ) {
      let result = false;
      if( nData ) {
        const dURL = System.graphURL();
        const key = nData.key;
        const ol = Root.getState( 'w_openWinList' );
        if( ol[dURL] && ol[dURL][key] ) {
          result = ol[dURL][key].isPin;
        }
      }
      return( result );
    }
    Window.getWindow = function( nData ) {
      // TODO: return the window managing the nData
      //       It should be possible to apply
      //       w = Window_getWindow( nData )
      //       src = Window_getSource( w )
    }
    Window.closeUnpinWindow = function() {
      const dURL = System.graphURL();
      const wl = Root.getState( 'w_winList' );
      const winList = wl[dURL];
      if( winList ) {
        for( const key in winList ) {
          const win = winList[key];
          if( win ) {
            const nData = { key };
            if( !Window_isPin( nData ) ) {
              win.close();
            }
          }
        }
      }
    }
    Window.closeAllWindow = function() {
      const dURL = System.graphURL();
      const wl = Root.getState( 'w_winList' );
      const winList = wl[dURL];
      if( winList ) {
        for( const key in winList ) {
          const win = winList[key];
          if( win ) {
            win.close();
          }
        }
      }
    }
    Window._getLocation = function( nData ) {
      let result = [10, 10, 250, 200]; // Default window pos/size
      if( nData ) {
        const dURL = System.graphURL();
        const pl = Root.getState( 'w_positionWinList' );
        let winList = pl[dURL];
        if( winList ) {
          const key = nData.key;
          const winPos = winList[key];
          if( winPos ) {
            result = winPos;
          }
        }
      }
      return( result );
    }
    Window._setLocation = function( nData, winPos ) {
      if( nData ) {
        const dURL = System.graphURL();
        const pl = Root.getState( 'w_positionWinList' );
        if( !pl[dURL] ) {
          pl[dURL] = {};
        }
        const key = nData.key;
        pl[dURL][key] = winPos;
      }
    }
    Window._setPosition = function( nData, x, y ) {
      if( nData ) {
        const dURL = System.graphURL();
        const pl = Root.getState( 'w_positionWinList' );
        if( !pl[dURL] ) {
          pl[dURL] = {};
        }
        const key = nData.key;
        if( pl[dURL][key] ) {
          pl[dURL][key][0] = x;
          pl[dURL][key][1] = y;
        } else {
          pl[dURL][key] = [x, y, 250, 200];
        }
      }
    }
    Window._setSize = function( nData, w, h ) {
      if( nData ) {
        const dURL = System.graphURL();
        const pl = Root.getState( 'w_positionWinList' );
        if( !pl[dURL] ) {
          pl[dURL] = {};
        }
        const key = nData.key;
        if( pl[dURL][key] ) {
          pl[dURL][key][2] = w;
          pl[dURL][key][3] = h;
        } else {
          pl[dURL][key] = [10, 10, w, h];
        }
      }
    }
    Window._setOpenWindow = function( nData, win ) {
      if( nData ) {
        const dURL = System.graphURL();
        const ol = Root.getState( 'w_openWinList' );
        if( !ol[dURL] ) {
          ol[dURL] = {}; 
        }
        const key = nData.key;
        ol[dURL][key] = { isPin: false };

        const wl = Root.getState( 'w_winList' );
        if( !wl[dURL] ) {
          wl[dURL] = {}; 
        }
        wl[dURL][key] = win;
      }
    }
    Window._tryFocusWindow = function( nData ) {
      let result = false;
      if( nData ) {
        const dURL = System.graphURL();
        const wl = Root.getState( 'w_winList' );
        const winList = wl[dURL];
        if( winList ) {
          const key = nData.key;
          const win = winList[key];
          if( win ) {
            win.focus( true );
            result = true;
          }
        }
      }
      return( result );
    }
    Window._createWinInfo = function( nData, winLoc ) {
      // Set window property
      const title = System.getNDataTitle( nData );
      const wClass = 'modern'; // class is a reserved keyword
      const background = nData.color || 'Plum';

      // Window info
      const winInfo = { 
        title, background, class: wClass,
        x: winLoc[0],     y: winLoc[1],
        width: winLoc[2], height: winLoc[3],
        onmove: (x,y)=>{
          Window._setPosition( nData, x, y );
        },
        onresize: (w,h)=>{
          Window._setSize( nData, w, h );
        },
        onclose: (e)=>{
          Window._removeWindow( nData );
        },
      };

      if( nData.fileURL ) {
        winInfo.url = nData.fileURL;
      }

      return( winInfo );
    }
    Window._customizeWindow = function( win, nDataKey ) {
      win.removeControl("wb-max");
      win.addControl({
          index: 2,
          class: "wb-like",
          image: "/fileServer/pictures/unpinnedw.svg",
          click: function( event, winbox ) {
              // the winbox instance will be passed as 2nd parameter
              // console.log(winbox.id);
              // "this" refers to the button which was clicked:
              this.classList.toggle( "active" );
              Window.tooglePin( nDataKey );
          }
      });
    }
    Window._removeWindow = function( nData ) {
      if( nData ) {
        const dURL = System.graphURL();
        // Remove from winList
        const wl = Root.getState( 'w_winList' );
        const winList = wl[dURL];
        if( winList ) {
          const key = nData.key;
          if( winList[key] ) {
            delete winList[key];
          }
        }
        // Remove from openList
        const ol = Root.getState( 'w_openWinList' );
        const openList = ol[dURL];
        if( openList ) {
          const key = nData.key;
          if( openList[key] ) {
            delete openList[key];
          }
        }
      }
    }
    //------------------------
    // API Functions
    //------------------------
    /**
     *  ContextMenu read-only properties:
     *   elId:        id of the element containing the ContextMenu
     *   el:          HTML element element containing the ContextMenu
     *   contextMenu: menu instance
     */
    ContextMenu._new = function( elId ) {
      let self = null;

      const el = document.getElementById( elId );
      if( el ) {
        self = Object.assign( {}, Graph );
        Root.mount( '/State/Transient/', 'c_contextMenu', self );

        // Create context menu object
    		const contextMenu = new HTMLMenu( elId );

        // Read-only ContextMenu property
        self.elId = elId;
        self.el = el;
        self.contextMenu = contextMenu;
      } else {
        console.error( `Error: div "${elId}" not found` );
      }

      return( self );
    }
  </script>
