 * This function takes 2 parameters:
 * - direction: 'next'/'previous'
 * - nodeData: current slide toolbar nodeData
 * 
 * This funtion move the view to the next/previous slide
 * if found, otherwise it goes to 'Slide 0'
 * 
 */
function navigateToSlide( direction, currSlideLabel ) {
  // Get current slide number
  const slideNumber = parseInt( currSlideLabel.split( ' ' )[1] );
  //console.log( 'Number', slideNumber );
  const ds = ( direction == 'Next'? 1: -1 );
  // Set next slide label
  const nextSlideLabel = `Slide ${slideNumber+ds}`;

  const dx = -200;
  const dy = -20;
  const found = setViewFromLabel( nextSlideLabel, dx, dy );
  if( !found ) {
    setViewFromLabel( 'Slide 0', dx, dy );
  }
}

function jumpToSlide( targetSlideLabel ) {
  const found = setViewFromLabel( targetSlideLabel, -100, -100 );
  if( !found ) {
    setViewFromLabel( 'Slide 1', -20, -20 );
  } 
}

function duplicateTemplateSlide( slideLabel ) {
  // Get main graph
  const g = getMainGraph();

  // Select slide template elements
  const templateNodeKey = [ 18, 17, 16 ];
  g.selectAllNodeByKey( templateNodeKey );

  // Create new slide
  g.doEditDuplicate();

  // Set slide label
  const firstNode = g.getFirstSelectedNodeData();
  const firstNodeKey = firstNode.key;
  setNodeDataField( firstNodeKey, 'label', slideLabel );
  g.moveSelectionRel( 900, 0 );
}","fileTypeName":"Javascript","fileType":"text/javascript","key":13,"location":"-4390 -1760"},
