if( name == 'history' ) {
  if( value.length ) {
    // Get the last message
    const lastMessage = value[value.length-1];
    const senderName = lastMessage.sender;
    console.log( 'BROADCAST SENDER:->>>>> ', senderName );
    // Get the list of all output names
    const outputNameList = graphData.dfe.geOutputNameList( nodeData );
    // Remove from the list the sender of the last message
    let filterNameList = outputNameList.filter( (n)=> !n.endsWith( senderName ) );
    // Select a random index for the remaining actors
    const idx = Math.floor( Math.random()*filterNameList.length );
    const outputName = filterNameList[idx];
    console.log( 'BROADCAST CHOICE:->>>>> ', outputName );
    // Store the output to the selected actor
    graphData.dfe.fireOutput( nodeData, outputName, value );
  }
}","fileTypeName":"Component","fileType":"text/javascript","iconURL":"","key":24,"location":"20560 -5240","group":25,"editorPosition":[1261,888,715,276]},
