if( name == 'in' ) {
  // Create a local variable
  // graphData -=> graph data in this current graph
  // graphData.dfe -=> data flow engine
  // .get() -=> retrieve a nodeData variable
  const idx = graphData.dfe.get( nodeData, 'idx', 0 );
  const outValue = graphData.dfe.get( nodeData, 'outValue', [] );
  // Get a node property
  const numberOfCycle = graphData.dfe.getProperty( nodeData, 'numberOfCycle', 0 );

  if( idx < numberOfCycle ) {
    const jsonValue = JSON.parse( value );
    outValue.push( jsonValue );
    graphData.dfe.set( nodeData, 'idx', idx+1 );
    graphData.dfe.fireOutput( nodeData, 'doNext' );
  } else if( idx == numberOfCycle ) {
    graphData.dfe.fireOutput( nodeData, 'out', outValue );
    graphData.dfe.set( nodeData, 'idx', 0 );
    graphData.dfe.set( nodeData, 'outValue', [] );
  }
}","fileTypeName":"Component","fileType":"text/javascript","iconURL":"","key":129,"location":"13070 -2610"},
