{"label":"Next Speaker Selection","category":"DataFlow_Component","size":"240 80","color":"LightSeaGreen","in_":[{"portId":"in","name":"in"}],"out_":[{"portId":"out","name":"out"},{"portId":"nextSpeaker","name":"nextSpeaker"}],"props_":[{"name":"computeBarrier","value":"","valueChanged":"true"},{"name":"actorList","value":"- Alex
- Bob
- Alice","unit":"","valueChanged":true}],"isFile":true,"fileContent":"var nodeData, name, value;

const sender = value.sender;
const receiver = value.receiver;

const nextSpeaker = getNextSpeaker( sender, receiver );  
graphData.dfe.fireOutput( nodeData, 'nextSpeaker', nextSpeaker );
graphData.dfe.fireOutput( nodeData, 'out', value );


function getNextSpeaker( sender, receiver ) {
  let result = '';
  if( receiver == 'All' ) {
    const actorList = graphData.dfe.getProperty( nodeData, 'actorList', '[]' );
    const actors = jsyaml.load( actorList );
    const filterActor = actors.filter( (n)=> !n.endsWith( sender ) );
    // Select a random index for the remaining actors
    const idx = Math.floor( Math.random()*filterActor.length );
    result = filterActor[idx];
  } else {
    result = receiver;
  }
  return( result );
}
