  const voiceList = getVoiceList();
  const voiceListStrArray = voiceList.map( (e ,i)=> `id: ${i}, name: ${e.name},  lang: ${e.lang}` );

  const shaKey = SHA1( JSON.stringify( voiceListStrArray ) );
  const key = shaKey.substring( 0, 6 );
  if( voiceMapList[key] ) {
    voiceMapList = voiceMapList[key];
  } else {
    voiceMapList = [];
  }
}

let voiceId = 0;
if( voiceMapList && voiceMapList.length ) {
  let voiceName = '';
  voiceMapList.forEach( (e)=> { if( value.sender == e.name ) voiceName = e.voice } );
  voiceId = getVoiceIdFronName( voiceName );
}

