  const voiceList = getVoiceList();
  for( let i = 0; i < voiceList.length; ++i ) {
    const voice = voiceList[i];
    if( voice.name == name ) {
      return( i );
    }
  }
  return( 0 );
}
function getVoiceList() {
  let result = [];

  result = speechSynthesis.getVoices();
  if( !result ) {
    result = speechSynthesis.getVoices();
  }
  return( result );
}
