{"label":"Get Nodes List","category":"DataFlow_Component","size":"240 80","color":"LightSeaGreen","in_":[{"portId":"in","name":"in"}],"out_":[{"portId":"out","name":"out"}],"props_":[{"name":"computeBarrier","value":"","valueChanged":true}],"isFile":true,"fileContent":"var nodeData, name, value;

const ros = value;

ros.getNodes( (response)=> {
  const len = response.length;
  const outValue = [];
  for( let i = 0; i < len; ++ i ) {
    const name = response[i];
    ros.getNodeDetails( name, function( details ) {
      const sInfo = { name, details };
      outValue.push( sInfo );
      if( outValue.length == len ) {
        graphData.dfe.fireOutput( nodeData, 'out', outValue );
      }
    });
  }
});","fileTypeName":"ROSGetActions","fileType":"text/javascript","iconURL":"","key":432,"location":"32450 5342.682","group":454},
