{"label":"Watch Dog 1","category":"DataFlow_Component","size":"240 80","color":"LightSeaGreen","in_":[{"portId":"in","name":"in"},{"portId":"start","name":"start"},{"portId":"timeOutIn","name":"timeOutIn"}],"out_":[{"portId":"out","name":"out"}],"props_":[{"name":"computeBarrier","value":"","valueChanged":true},{"name":"timeOut","value":"6","unit":"s","valueChanged":"true"}],"isFile":true,"fileContent":"var nodeData, name, value;

if( name == 'in' ) {
  const timer = graphData.dfe.get( nodeData, 'timer', null );
  if( timer ) {
    clearTimeout( timer );
    graphData.dfe.fireOutput( nodeData, 'out', value );
  }
} else if( name == 'start' ) {
  const timeOut = graphData.dfe.getProperty( nodeData, 'timeOut', 4 );
  const timer = setTimeout( ()=> {
                               const outValue = graphData.dfe.getInput( nodeData, 'timeOutIn', null );
                               graphData.dfe.fireOutput( nodeData, 'out', outValue );
                               graphData.dfe.set( nodeData, 'timer', null );
                             }, 
                             parseFloat( timeOut )*1000 );
  graphData.dfe.set( nodeData, 'timer', timer );
}","fileTypeName":"WatchDog","fileType":"text/javascript","iconURL":"","key":516,"location":"22260 2803.8","editorPosition":[1537,1058,862,376],"group":503},
