{"label":"Select filler","category":"DataFlow_Component","size":"240 80","color":"LightSeaGreen","in_":[{"portId":"doNextFiller","name":"doNextFiller"},{"portId":"filler","name":"filler"}],"out_":[{"portId":"out","name":"out"}],"props_":[{"name":"computeBarrier","value":"","valueChanged":true},{"name":"fillerType","value":"waiting","unit":"","valueChanged":true},{"name":"sender","value":"Alex","unit":"","valueChanged":true},{"name":"receiver","value":"All","unit":"","valueChanged":true}],"isFile":true,"fileContent":"var nodeData, name, value;

if( name == 'doNextFiller' ) {
  const filler = graphData.dfe.getInput( nodeData, 'filler', {} );
  const fillerType = graphData.dfe.getProperty( nodeData, 'fillerType', '' );

  const fillerList = filler[fillerType];
  if( fillerList && fillerList.length ) {
    // Get random index in fillerList
    const idx = Math.floor( Math.random()*fillerList.length );
    // Get fillet text
    const text = fillerList[idx];
    const sender = graphData.dfe.getProperty( nodeData, 'sender', 'All' );
    const receiver = graphData.dfe.getProperty( nodeData, 'receiver', 'All' );
    const outValue = { sender, receiver, text };
    graphData.dfe.fireOutput( nodeData, 'out', outValue )
  }
}","fileTypeName":"Component","fileType":"text/javascript","iconURL":"","key":535,"location":"24210 2889.9","group":747},
