  setGateColor( nodeData, value );
} else if( name == 'in' ) {
  // Get gate value
  const isGateOn = ( nodeData.color != 'red' ); // True if color different than red

  // Generate ouptut if gate on
  if( isGateOn ) {
    // Generate an output trigger
    graphData.dfe.fireOutput( nodeData, 'out', value );
  }
}

function setGateColor( nodeData, value ) {
  const gateOn = graphData.dfe.getProperty( nodeData, 'gateOn', false );
  // Set color
  const color = ( value == gateOn? 'green': 'red' );
  setNodeDataField( nodeData.key, 'color', color );
}","fileTypeName":"Gate Input","fileType":"text/javascript","iconURL":"","key":632,"location":"39820 910","editorPosition":[939,656,461,256]},
