const ros = value;
ros.getTopics( (response)=> {
  //const topicList = response.topics;
  //const typeList = response.types;
  const len = response.topics.length;
  const outValue = [];
  for( let i = 0; i < len; ++ i ) {
    const name = response.topics[i];
    ros.getTopicType( name, function( type ) {
      const sInfo = { name, type };
      outValue.push( sInfo );
      if( outValue.length == len ) {
        graphData.dfe.fireOutput( nodeData, 'out', outValue );
      }
    });
  }
});","fileTypeName":"ROSGetTopics","fileType":"text/javascript","iconURL":"","key":875,"location":"32550 7250","group":454},
