Engine_set( nodeData, 'command', value );

//--------------------------------
// Environment State
//--------------------------------

var initState = {
  gaze: {
    fovea: [],
    perif: [],
  },
  hand_left: {
    holdsObject: [],
    isHoldByObject: [],
  },
  hand_right:  {
    holdsObject: [],
    isHoldByObject: [],
  },
  lab: {
    holdsObject:['table_1'],
  },
  table_1: {
    holdsObject: ['fridge_1','microwave_1','toaster_1','plate_1','glass_1','mug_1','pitcher_1','pitcher_2','tray_1'],
    isHoldByObject: ['lab'],
  },
  fridge_1: {
    holdsObject: [],
    isHoldByObject: ['table_1'],
    closure: 'closed',
    power: 'on',
    temperature: -5,
  },
  microwave_1: {
    holdsObject: [],
    isHoldByObject: ['table_1'],
    closure: 'closed',
    power: 'off',
    temperature: 20,
  },
  toaster_1: {
    holdsObject: [],
    isHoldByObject: ['table_1'],
    power: 'off',
    temperature: 20,
  },
  plate_1: {
    holdsObjectF: ['toast_1'],
    isHoldByObject: ['table_1'],
    temperature: 20,
  },
  toast_1: {
    holdsObject: [],
    isHoldByObject: ['plate_1'],
    temperature: 20,
    preparation: 'raw'
  },
  cola_bottle_1: {
    holdsObject: [],
    isHoldByObject: ['plate_1'],
    closure: 'closed',
    temperature: 20,
    volume: 0.5,
    fillLevel: 0.5,
    holdsLiquid: ['coca-cola'],
  },
  glass_1: {
    holdsObject: [],
    isHoldByObject: ['table_1'],
    temperature: 20,
    volume: 0.1,
    fillLevel: 0.0,
    holdsLiquid: [],
  },
  mug_1: {
    holdsObject: [],
    isHoldByObject: ['table_1'],
    temperature: 'ambiant',
    volume: 0.1,
    fillLevel: 0.0,
    holdsLiquid: [],
  },
  pitcher_1: {
    holdsObject: [],
    isHoldByObject: ['table_1'],
    temperature: 20,
    volume: 1.0,
    fillLevel: 1.0,
    holdsLiquid: ['water'],
  },
  pitcher_2: {
    holdsObject: [],
    isHoldByObject: ['table_1'],
    temperature: 20,
    volume: 1.0,
    fillLevel: 1.0,
    holdsLiquid: ['coffee'],
  },
  tray_1: {
    holdsObject: [],
    isHoldByObject: ['table_1'],
  },
  tea_bag_1: {
    holdsObject: [],
    isHoldByObject: ['table_1'],
  },
};
var state = Engine_get( nodeData, 'state', initState );

function updateEnvironmentState() {
  let response = '';
  let commandStr = Engine_get( nodeData, 'command' );
  const toRobi = '[to Robi] ';
  const idx = commandStr.indexOf(toRobi);
  if( idx != -1 ) {
    commandStr = commandStr.substring( idx + toRobi.length );
  }
  state = Engine_get( nodeData, 'state' );
  tokenList = commandStr.split( ' ' );
  if( tokenList ) {
    const commandName = tokenList[0] || '';
    const objectName1 = tokenList[1] || '';
    let objectName2 = tokenList[2] || '';
    let handName = tokenList[3] || '';
    switch( commandName ) {
      case 'get':
        if( handName ) {
          if( isHandEmpty( handName ) ) {
            getObjectInHand( handName, objectName1 );
            response = `[from Robi] ${objectName1} is in ${handName}`;
          }
        } else {
          if( isHandEmpty( 'hand_right' ) ) {
            getObjectInHand( 'hand_right', objectName1 );
            response = `[from Robi] ${objectName1} is in hand_right`;
          } else if( isHandEmpty( 'hand_left' ) ) {
            getObjectInHand( 'hand_left', objectName1 );
            response = `[from Robi] ${objectName1} is in hand_left`;
          }
        }
        break;
      case 'put':
        if( isObjectInHand( 'hand_right', objectName1 ) ) {
          objectName2 = putObjectOnObject( 'hand_right', objectName2 );
        } else if( isObjectInHand( 'hand_left', objectName1 ) ) {
          objectName2 = putObjectOnObject( 'hand_left', objectName2 );
        }
        response = `[from Robi] ${objectName1} is in/on ${objectName2}`;
        break;
      case 'gaze':
        gazeAtObject( objectName1 );
        response = `[from Robi] ${objectName1} is gazed at`;
        break;
      case 'open_door': // valid on microwave and fridge
        //if( !isObjectOpened( objectName1 ) ) {
        setClosure( objectName1, 'opened' );
        response = `[from Robi] ${objectName1} is opened`;
        //}
        break;
      case 'close_door':
        //if( isObjectOpened( objectName1 ) ) {
        setClosure( objectName1, 'closed' );
        response = `[from Robi] ${objectName1} is closed`;
        //}
        break;
      case 'finger_push': // valid on toaster and microwave
        if( isObjectPoweredOn( objectName1 ) ) {
          setPower( objectName1, 'off' );
        } else {
          setPower( objectName1, 'on' );
        }
        break;
      case 'pour':
        pourObject( objectName1, objectName2 );
        break;
      case 'screw':
        if( isObjectOpened( objectName1 ) ) {
          setClosure( objectName1, 'closed' );
        } else {
          setClosure( objectName1, 'opened' );
        }
    }
  }
  console.log(state)
  return( response );
}
function isHandEmpty( handName ) {
  return( state[handName].holdsObject.length == 0 );
}
function isBothHandEmpty() {
  return ( isHandEmpty( 'hand_right' ) && isHandEmpty( 'hand_left' ) );
}
function isObjectInHand( handName, objectName ) {
  return( state[handName].holdsObject.includes( objectName ) );
}
function isObjectOpened( objectName ) {
  return( state[objectName].closure == 'opened'? true: false );
}
function setClosure( objectName, status ) {
  state[objectName].closure = status;
}
function isObjectPoweredOn( objectName ) {
  return( state[objectName].power == 'on'? true: false );
}
function setPower( objectName, status ) {
  state[objectName].power = status;
}
function pourObject( fromName1, toName2 ) {
  if( state[fromName1].fillLevel >= state[toName2].volume ) {
    state[fromName1].fillLevel -= state[toName2].volume;
    state[fromName1].fillLevel = Math.max( 0, state[fromName1].fillLevel );
  }
}
function getObjectInHand( handName, objectName ) {
  // Hand now holds objectName 
  state[handName].holdsObject.push( objectName );
  // ObjectName is now holded by hand
  const previousHolder = state[objectName].isHoldByObject.shift();
  state[objectName].isHoldByObject.push( handName );
  // previousHolder does not hold objectName anymore
  let objectList = state[previousHolder].holdsObject;
  state[previousHolder].holdsObject = objectList.filter( (d)=> d != objectName );
}
function putObjectOnObject( handName, containerName ) {
  containerName = containerName || 'table_1';
  console.log('putObjectOnObject', handName, containerName);
  // Hand now holds nothing 
  const objectName = state[handName].holdsObject.shift();
  // ObjectName is now holded by containerName
  state[objectName].isHoldByObject.shift();
  state[objectName].isHoldByObject.push( containerName );
  // containerName now holds objectName 
  state[containerName].holdsObject.push( objectName );
  return( containerName );
}
function gazeAtObject( objectName ) {
  state.gaze.fovea = [objectName];
  // Get parent object
  const parent = state[objectName].isHoldByObject;
  state.gaze.perif = state[parent[0]].holdsObject;
}

function checkIfCommandIsValid( value ) {
  const toRobi = '[to Robi] ';
  const idx = value.indexOf(toRobi);
  let errorMsg = '';
  let isValid = true;
  let command = '';
  if( idx != -1 ) {
    command = value.substring( idx + toRobi.length );
    if( command ) {
      isValid = true;
    } else {
      isValid = false;
      errorMsg = '[from Robi] no command sent';
    }
  } else {
    isValid = false;
    errorMsg = '[from Robi] wrong command. The command must have the syntax: [to Robi] <command>';
  }
  const tokenList = command.split( ' ' );
  if( tokenList && isValid ) {
    const commandName = tokenList[0] || '';
    const objectName1 = tokenList[1] || '';
    let objectName2 = tokenList[2] || '';
    let handName = tokenList[3] || '';
    switch( commandName ) {    
      case 'put':
        if( isBothHandEmpty() ) {
          errorMsg = '[from Robi] cannot "put" because both hands are empty';
          isValid = false;
        }
      break;
    }
  }
  return( { command, isValid, errorMsg } );
}
//--------------------------------
// Communication with robot server
//--------------------------------
function sendCommand() {
  // Value is the command supposed to be send to the robot client

  // Check if the requested command is a valid one in the current
  // state of the environment
  const cmdCheckInfo = checkIfCommandIsValid( value );
  if( cmdCheckInfo.isValid ) {
    ws.send( cmdCheckInfo.command );
  } else {
    Engine_fireOutput( nodeData, 'response', cmdCheckInfo.errorMsg );
    const stateMsg = JSON.stringify( state, null, 2 );
    Engine_fireOutput( nodeData, 'environmentState', stateMsg );
  }
}

// Instanciate websocket only one time
var ws = Engine_get( nodeData, 'ws', null );
if( !ws ) {
  ws = newWS( sendCommand );
  Engine_set( nodeData, 'ws', ws );
} else {
  checkSocketState( ws, 
  );
}

function newWS( onConnected  ) {
  const wsURL = appData.me.getProperty( modelId, nodeData, 'wsURL' );
  let ws = null;
  ws = new WebSocket( `ws://${wsURL}/` );
  if( ws ) {
    ws.onmessage = (event)=> {
      const msg = event.data;
      Engine_fireOutput( nodeData, 'status', 'Received response' );
      if( msg == 'Success' ) {
      const response = updateEnvironmentState();
        Engine_fireOutput( nodeData, 'response', response );
        const stateMsg = JSON.stringify( state, null, 2 );
        Engine_fireOutput( nodeData, 'environmentState', stateMsg );
      }
    };
    ws.onopen = (event)=> {
      Engine_fireOutput( nodeData, 'status', 'Open websocket' );
      if( onConnected ) {
        onConnected();
      } 
    };
    ws.onclose = (event)=> {
      ws = null;
      Engine_set( nodeData, 'ws', ws );
      Engine_fireOutput( nodeData, 'status', 'Server not running' );
    };
    ws.onerror = (event)=> {
      Engine_fireOutput( nodeData, 'status', 'Error' );
    };
    checkSocketState( ws );
  }
  return( ws );
}
function checkSocketState( ws, onConnected ) {
  console.log( 'readyState:', ws.readyState );
  switch( ws.readyState ){
    case 0: // CONNECTING: Socket has been created. The connection is not yet open.
      Engine_fireOutput( nodeData, 'status', 'Connecting to websocket' );
      break;
    case 1: // OPEN: The connection is open and ready to communicate.
      Engine_fireOutput( nodeData, 'status', 'Opened websocket' );
      if( onConnected ) {
        onConnected();
      }
      break;
    case 2: // CLOSING: The connection is in the process of closing.
      Engine_fireOutput( nodeData, 'status', 'Closing websocket' );
      break;
    case 3: // CLOSED: The connection is closed or couldn't be opened.
      Engine_fireOutput( nodeData, 'status', 'Server not running' );
      break;
  }
}
