{"label":"OpenAI GPT","category":"AIFlow_LLM","size":"240 80","color":"LightSeaGreen","in":[{"portId":"system"},{"portId":"inHistory"},{"portId":"user"}],"out":[{"portId":"outHistory"},{"portId":"assistant"}],"rows":[{"name":"model","value":"gpt-3.5-turbo-0613","unit":"","valueChanged":true},{"name":"isPersistent","value":"false"},{"name":"maxConcurency","value":"5","unit":""},{"name":"maxRetry","value":"3","unit":""},{"name":"timeOut","value":"4","unit":"s"}],"isFile":true,"fileContent":"var nodeData, name, value;
//debugger
// Check input port
formatInput( nodeData, name, value );
// switch( name ) {
//   case 'system': {
//     Engine_setInput( nodeData, 'system', value );
//   }
//   break;

//   case 'inHistory': {
//     Engine_setInput( nodeData, 'inHistory', value );
//   }
//   break;

//   case 'user': {
//     Engine_setInput( nodeData, 'inHistory', value );
//   break;
// }

const systemValue = Engine_getInput( nodeData, 'system', [] );
const inHistoryValue = Engine_getInput( nodeData, 'inHistory', null );
const userValue = Engine_getInput( nodeData, 'user', null );
if( ( inHistoryValue == undefined && userValue ) ||
    ( inHistoryValue && userValue ) ) {

  // Merge all input array into a single message
  const messages = [];
  const history = [];
  if( inHistoryValue )
    history.push( ...inHistoryValue );
  if( userValue )
    history.push( ...userValue );
  if( systemValue )
    messages.push( ...systemValue );
  if( history )
    messages.push( ...history );

  // Define model from the component property
  const model = appData.me.getProperty( modelId, nodeData, 'model' );
  appData.chatGPT.setParam( 'model', model );

  // Ask chatGPT
  appData.chatGPT.getResponse( messages, (response)=>{
    const defaultIsJSON = typeof( userValue ) == 'object';
    const isJSON = Engine_get( nodeData, 'isJSON', defaultIsJSON );

    const outValue = formatResponse( history, isJSON, response );
    Engine_fireOutput( nodeData, 'outHistory', outValue );
    const assistantValue = formatResponse( [], isJSON, response )
    Engine_fireOutput( nodeData, 'assistant', assistantValue );
  });
  // Clean inputs
  Engine_clearInputs( nodeData, [ 'inHistory', 'user' ] );
}

function formatInput( nodedata, name, value ) {
    // Format input value
    let isJSON = false;
    // Input value is an oject or an array
    if( typeof( value ) == 'object' ) {
      isJSON = true;
      // Input value is not an array
      if( !Array.isArray( value ) ) {
        value = [value];
      } 
    } else {
      // Input value is a string
      value = [ { role: name, content: value } ];
    }
    Engine_set( nodeData, 'isJSON', isJSON );
    // Save formatted input value
    Engine_setInput( nodeData, name, value );
}
function formatResponse( value, isJSON, response ) {
  let outValue  = null;
  const isvalueEmpty = ( value.length == 0 );

  if( isJSON ) {
    value.push( { "role": "assistant", "content":response} );
    if( !isvalueEmpty ) {
      value.push( { "role": "user", "content":""} );
    }
    // outValue = JSON.stringify( value, null, 2 );
    outValue = value;

  // Case of a string response
  } else {
    if( value.startsWith( 'user>' ) ) {
      if( !response.startsWith( 'assistant>' ) ) {
        response = 'assistant> '+response;
      }
    }
    if( !isvalueEmpty ) {
      outValue = value + '\n\n' + response + '\n\nuser> ';
    } else {
      outValue = response;
    }
  }
  return( outValue );
}","fileTypeName":"LLM","fileType":"text/javascript","key":28,"location":"7600 140","editorPosition":[565,48,551,1169]},
