{"label":"Plan Executor","category":"AIFlow_Component","size":"240 80","color":"LightSeaGreen","in":[{"portId":"plan"},{"portId":"feedback"}],"out":[{"portId":"rocoToAlex"},{"portId":"rocoToRopa"},{"portId":"rocoToRobi"},{"portId":"outIndex"}],"rows":[{"name":"isPersistent","value":"false"},{"name":"computeBarrier","value":"feedback","unit":"","valueChanged":true}],"isFile":true,"fileContent":"var nodeData, name, value;
//debugger
// Update input values
const inState = Engine_updateInput( nodeData, name, value );

// True if all inputs arrived
if( inState.isCompute ) {
  const plan = Engine_getInput( nodeData, 'plan', '' );
  const feedback = Engine_getInput( nodeData, 'feedback', '' );
  let counter = Engine_get( nodeData, 'commandIndex', 0 );
  if( feedback ) {
    Engine_set( nodeData, 'commandIndex', counter );
    if( plan ) {
      execute( plan, feedback, counter);
    }
  }
}
function execute( plan, feedback, cmdIndex ) {
  const commandList = plan.split('\n');
  const toRocoStr = '[Ropa to Roco] ';
  const errorStr = '[Robi to Roco] Error:';
  if( feedback.startsWith( errorStr ) ) {
    cmdIndex--;
    Engine_set( nodeData, 'commandIndex', cmdIndex );
    const lastCommand = commandList[cmdIndex].slice( toRocoStr.length );
    const errorMessage = feedback.slice('[Roco to Ropa] Error:'.length).trim();
    const msg = `[Roco to Ropa] Error: ${errorMessage}`;
    Engine_fireOutput( nodeData, 'rocoToRopa', msg );
    Engine_fireOutput( nodeData, 'outIndex', ''+cmdIndex );
  } else if( cmdIndex >= 0 && cmdIndex < commandList.length ) {
    const command = commandList[cmdIndex].slice( toRocoStr.length );
    cmdIndex++;
    Engine_set( nodeData, 'commandIndex', cmdIndex );
    const msg = `[Roco to Robi] ${command}`;
    Engine_fireOutput( nodeData, 'rocoToRobi', msg );
    Engine_fireOutput( nodeData, 'outIndex', ''+cmdIndex );
  } else if( cmdIndex >= commandList.length ) {
    cmdIndex = 0;
    Engine_set( nodeData, 'commandIndex', cmdIndex );
    const msg = `[Roco to Alex] Plan has been executed successfully.`;
    Engine_fireOutput( nodeData, 'rocoToAlex', msg );
    Engine_fireOutput( nodeData, 'outIndex', ''+cmdIndex );
  }
}
