    // outValue = JSON.stringify( value, null, 2 );
    outValue = value;

  // Case of a string response
  } else {
    if( value.startsWith( 'user>' ) || value.startsWith( '[' ) ) {
      if( !response.startsWith( 'assistant>' ) ) {
        response = 'assistant> '+response;
      }
    }
    if( !isvalueEmpty ) {
      outValue = value + '\n\n' + response + '\n\nuser> ';
    } else {
      outValue = response;
    }
  }
  return( outValue );
