{"label":"Update Environment State","category":"AIFlow_Component","size":"250 100","color":"LightSeaGreen","in":[{"portId":"oldState"},{"portId":"simCommand"},{"portId":"simFeedback"}],"out":[{"portId":"newState"},{"portId":"feedback"},{"portId":"out"}],"rows":[{"name":"isPersistent","value":"false"},{"name":"computeBarrier","value":"simFeedback","unit":"","valueChanged":"true"}],"isFile":true,"fileContent":"var nodeData, name, value;
// Update input values
var inState = Engine_updateInput( nodeData, name, value );

var state = JSON.parse( Engine_getInput( nodeData, 'oldState', '{}' ) );
var command = Engine_getInput( nodeData, 'simCommand', '' );
var simFeedback = Engine_getInput( nodeData, 'simFeedback', '' );

// True if all inputs arrived
if( inState.isCompute ) {
  const toRoco = '[Robi to Roco] ';
  if( simFeedback == 'Success' ) {
    simFeedback = updateEnvironmentState( command );
  } 
  const feedback = `${toRoco}${simFeedback}`;
  let newState = JSON.stringify( state, null, 2 );
  console.log(newState);
  Engine_fireOutput( nodeData, 'feedback', feedback );
  Engine_fireOutput( nodeData, 'newState', newState );
}

function updateEnvironmentState( command ) {
  tokenList = command.split( ' ' );
  if( tokenList ) {
    const commandName = tokenList[0] || '';
    const objectName1 = tokenList[1] || '';
    let objectName2 = tokenList[2] || '';
    let handName = tokenList[3] || '';
    switch( commandName ) {
      case 'get':
        getObjectInHand( handName, objectName1 );
        response = `Success: ${objectName1} is in ${handName}.`;
        break;
      case 'put':
        if( isObjectInHand( 'hand_right', objectName1 ) ) {
          objectName2 = putObjectOnObject( 'hand_right', objectName2 );
        } else if( isObjectInHand( 'hand_left', objectName1 ) ) {
          objectName2 = putObjectOnObject( 'hand_left', objectName2 );
        }
        response = `Success: ${objectName1} is in/on ${objectName2}.`;
        break;
      case 'gaze':
        gazeAtObject( objectName1 );
        response = `Success:  ${objectName1} is gazed at.`;
        break;
      case 'open_door':
        setClosure( objectName1, 'opened' );
        response = `Success: ${objectName1} is opened.`;
        break;
      case 'close_door':
        setClosure( objectName1, 'closed' );
        response = `Success: ${objectName1} is closed.`;
        break;
      case 'finger_push':
        if( isObjectPoweredOn( objectName1 ) ) {
          setPower( objectName1, 'off' );
          response = `Success: ${objectName1} is switched on.`;
        } else {
          setPower( objectName1, 'on' );
          response = `Success: ${objectName1} is switched off.`;
        }
        break;
      case 'pour':
        const liquid = getLiquidInObject( objectName1 );
        const level = pourObject( objectName1, objectName2 );
        if( level > 0 ) {
          response = `Success: Poured ${level} litter of ${liquid} from ${objectName1} into ${objectName2}.`;
        } else {
          response = `Error: ${objectName1} is empty.`;
        }
        break;
      case 'screw':
        if( isObjectOpened( objectName1 ) ) {
          setClosure( objectName1, 'closed' );
          response = `Success: ${objectName1} is closed.`;
        } else {
          setClosure( objectName1, 'opened' );
          response = `Success: ${objectName1} is opened.`;
        }
        break;
    }
  }
  return( response );
}
function isHandEmpty( handName ) {
  return( state[handName].holdsObject.length == 0 );
}
function isBothHandEmpty() {
  return ( isHandEmpty( 'hand_right' ) && isHandEmpty( 'hand_left' ) );
}
function isObjectInHand( handName, objectName ) {
  return( state[handName].holdsObject.includes( objectName ) );
}
function isObjectOpened( objectName ) {
  return( state[objectName].closure == 'opened'? true: false );
}
function setClosure( objectName, status ) {
  state[objectName].closure = status;
}
function isObjectPoweredOn( objectName ) {
  return( state[objectName].power == 'on'? true: false );
}
function setPower( objectName, status ) {
  state[objectName].power = status;
}
function pourObject( fromName, toName ) {
  if( state[fromName].fillLevel >= 0 ) {
    state[toName].fillLevel = Math.min( state[toName].volume, state[fromName].fillLevel );
    state[fromName].fillLevel -= state[toName].fillLevel;
    state[fromName].fillLevel = Math.max( 0, state[fromName].fillLevel );
    return( state[toName].fillLevel );
  }
}
function getLiquidInObject( objectName ) {
  return( state[objectName].holdsLiquid[0] );
}
function getObjectInHand( handName, objectName ) {
  // Hand now holds objectName 
  state[handName].holdsObject.push( objectName );
  // ObjectName is now holded by hand
  const previousHolder = state[objectName].isHoldByObject.shift();
  state[objectName].isHoldByObject.push( handName );
  // previousHolder does not hold objectName anymore
  let objectList = state[previousHolder].holdsObject;
  state[previousHolder].holdsObject = objectList.filter( (d)=> d != objectName );
}
function putObjectOnObject( handName, containerName ) {
  containerName = containerName || 'table_1';
  console.log('putObjectOnObject', handName, containerName);
  // Hand now holds nothing 
  const objectName = state[handName].holdsObject.shift();
  // ObjectName is now holded by containerName
  state[objectName].isHoldByObject.shift();
  state[objectName].isHoldByObject.push( containerName );
  // containerName now holds objectName 
  state[containerName].holdsObject.push( objectName );
  return( containerName );
}
function gazeAtObject( objectName ) {
  state.gaze.fovea = [objectName];
  // Get parent object
  const parent = state[objectName].isHoldByObject;
  state.gaze.perif = state[parent[0]].holdsObject;
}","fileTypeName":"Component","fileType":"text/javascript","iconURL":"","key":280,"location":"8451.896 14585.627999999999","editorPosition":[3,311,911,1600],"group":364},
