{"label":"Robot Client","category":"AIFlow_Component","size":"240 80","color":"LightSeaGreen","in":[{"portId":"message"}],"out":[{"portId":"feedback"},{"portId":"status"}],"rows":[{"name":"isPersistent","value":"false"},{"name":"wsURL","value":"localhost:35000","unit":"","valueChanged":"true"}],"isFile":true,"fileContent":"var nodeData, name, value;
Engine_set( nodeData, 'command', value );


//--------------------------------
// Communication with robot server
//--------------------------------
function sendCommand() {
  // Value is the command supposed to be send to the robot client
  ws.send( value );
}

// Instanciate websocket only one time
var ws = Engine_get( nodeData, 'ws', null );
if( !ws ) {
  ws = newWS( sendCommand );
  Engine_set( nodeData, 'ws', ws );
} else {
  checkSocketState( ws, sendCommand );
}

function newWS( onConnected  ) {
  const wsURL = appData.me.getProperty( modelId, nodeData, 'wsURL' );
  let ws = null;
  ws = new WebSocket( `ws://${wsURL}/` );
  if( ws ) {
    ws.onmessage = (event)=> {
      const msg = event.data;
      Engine_fireOutput( nodeData, 'status', 'Received response' );
      if( msg == 'Success' ) {
        Engine_fireOutput( nodeData, 'feedback', msg );
      }
    };
    ws.onopen = (event)=> {
      Engine_fireOutput( nodeData, 'status', 'Open websocket' );
      if( onConnected ) {
        onConnected();
      } 
    };
    ws.onclose = (event)=> {
      ws = null;
      Engine_set( nodeData, 'ws', ws );
      Engine_fireOutput( nodeData, 'status', 'Server not running' );
    };
    ws.onerror = (event)=> {
      Engine_fireOutput( nodeData, 'status', 'Error' );
    };
    checkSocketState( ws );
  }
  return( ws );
}
function checkSocketState( ws, onConnected ) {
  console.log( 'readyState:', ws.readyState );
  switch( ws.readyState ){
    case 0: // CONNECTING: Socket has been created. The connection is not yet open.
      Engine_fireOutput( nodeData, 'status', 'Connecting to websocket' );
      break;
    case 1: // OPEN: The connection is open and ready to communicate.
      Engine_fireOutput( nodeData, 'status', 'Opened websocket' );
      if( onConnected ) {
        onConnected();
      }
      break;
    case 2: // CLOSING: The connection is in the process of closing.
      Engine_fireOutput( nodeData, 'status', 'Closing websocket' );
      break;
    case 3: // CLOSED: The connection is closed or couldn't be opened.
      Engine_fireOutput( nodeData, 'status', 'Server not running' );
      break;
  }
}
