{"label":"Status Div","category":"CodeFlow_Code","size":"180 100","color":"orange","rows":[],"in":[{"portId":"persistentFields"},{"portId":"transientFields"}],"out":[{"portId":"source"},{"portId":"declaration"},{"portId":"scriptOnLoad"},{"portId":"section"}],"isFile":true,"fileTypeName":"Javascript","fileType":"text/javascript","key":71,"location":"-3210 1736.5310000000002","fileContent":"//[# Begin source #]

window.addEventListener( 'load', ()=>{
  // Set initialization function
  document.body.style.setProperty( 'background', '#1d1f21', 'important' ); 
  onLoad();
});

//[# Begin declaration #]
const hgRoot = {
  status: {
    persistent: {
      isReadOnly: false,
      //[# Insert persistentFields #]
    },
    transient: {
      statusEl: null,
      //[# Insert transientFields #]
    },
  },
};
//[# End declaration #]

function onLoad() {
  //[# Begin scriptOnLoad #]
  State_init( 'statusDiv' );
  //[# End scriptOnLoad #]
}

//[# Begin section #]
function State_init( divId ) {
  State_set( 'statusEl', document.getElementById( divId ) );
}
function State_set( name, value ) {
  if( hgRoot.status.persistent[name] !== undefined ) {
    hgRoot.status.persistent[name] = value;
  } else if( hgRoot.status.transient[name] !== undefined ){
    hgRoot.status.transient[name] = value;
  } else {
    console.error( `Error: field ${name} not found in state` );
  }
}
function State_get( name ) {
  if( hgRoot.status.persistent[name] !== undefined ) {
    return( hgRoot.status.persistent[name] );
  } else if( hgRoot.status.transient[name] !== undefined ) {
    return( hgRoot.status.transient[name] );
  } else {
    console.error( `Error: field ${name} not found in state` );
  }
}
function State_toogleReadOnly() {
  const ro = !State_get( 'isReadOnly' );
  State_set( 'isReadOnly', ro );
  const statusEl = State_get( 'statusEl' );
  statusEl.style['border-style'] = ( ro? 'dashed': 'solid' );
}
function State_setError() {
  const statusEl = State_get( 'statusEl' );
  statusEl.className = 'error';
}
function State_setReady() {
  const statusEl = State_get( 'statusEl' );
  statusEl.className = 'default';
}
function State_setLoading() {
  const statusEl = State_get( 'statusEl' );
  statusEl.className = 'loading';
}
function State_setNeedSave() {
  if( !State_get( 'isReadOnly' ) ) {
    const statusEl = State_get( 'statusEl' );
    statusEl.className = 'warning';
  }
}
function State_setSaved() {
  if( !State_get( 'isReadOnly' ) ) {
    const statusEl = State_get( 'statusEl' );
  statusEl.className = 'saved';
  }
}
//[# End section #]

function State_setEditorChanged() {
  if( !State_get( 'isReadOnly' ) ) {
    State_setNeedSave();
    setTimeout( State_setSaved, 1*1000 );
  }
}
