{"label":"Diagram Selection Actions","category":"CodeFlow_Code","size":"180 100","color":"orange","rows":[],"in":[],"out":[{"portId":"section"}],"isFile":true,"fileTypeName":"JavaScript","fileType":"text/javascript","key":182,"location":"-4050 3577.5000000000005","fileContent":"//[# Begin section #]
//------------------------
// API Functions
//------------------------
function Diagram_getSelection() {
  let result = null;
  if( myDiagram ) {
    result = myDiagram.selection;
  }
  return( result );
}
function Diagram_getSelectedDataList() {
  let result = [];
  if( myDiagram ) {
    const selection = myDiagram.selection;
    const iterator = selection.iterator;
    while( iterator.next() ) {
      const node = iterator.value;
      if( node ) {
        const data = node.data;
        if( data ) {
          result.push( data );
        }
      }
    }
  }
  return( result );
}
function Diagram_getFirstSelectedData() {
  let result = null;
  if( myDiagram ) {
    const node = myDiagram.selection.first();
    if( node ) {
      result = node.data;
    }
  }
  return( result );
}
function Diagram_getSelectionCount() {
  let result = 0;
  if( myDiagram ) {
    result = myDiagram.selection.count;
  }
  return( result );
}
function Diagram_getProperty() {
  let result = '';

  // Get firt selected node
  const data = Diagram_getFirstSelectedData();
  if( data ) {
    const nodeType = ( data.linkToKey? 'LinkNode': 'Node' );
    result = `${nodeType} [${data.key}]: ${data.label || ''}\n`+
             `Category: ${data.category}`+
             ( data.group? `\nMember of group: [${data.group}]`: '' );
  } else {
    // Otherwise give info about the diagram
    if( myDiagram ) {
      // Tooltip info for the diagram's model
      const modelCount = myDiagram.model.nodeDataArray.length;
      const linkCount = myDiagram.model.linkDataArray.length;
      const graphURL = 'localhost';
  		result = `Model Info:\n`+
  		         `- #${modelCount} nodes\n`+
  		         `- #${linkCount} links\n`+
  		         `WorkSpace URL: ${graphURL}`;
    }
  }
  return( result );
}
function Diagram_showPropertyFromSelection() {
  alert( Diagram_getProperty() );
}
