{"label":"Diagram View Actions","category":"CodeFlow_Code","size":"180 100","color":"orange","rows":[],"in":[],"out":[{"portId":"declaration"},{"portId":"section"}],"isFile":true,"fileTypeName":"JavaScript","fileType":"text/javascript","key":186,"location":"-4050 3697.4000000000005","fileContent":"//[# Begin declaration #]
const viewList = [];
//[# End declaration #]

//[# Begin section #]
//------------------------
// API Functions
//------------------------
function Diagram_zoomToFit() {
  if( myDiagram ) {
    // Store last view in ViewLast
		viewList[0] = Diagram_getCurrentView();
		// Zoom to fit
    myDiagram.zoomToFit();
  }
}
function Diagram_storeView( index ) {
  viewList[index] = Diagram_getCurrentView();
}
function Diagram_goToView( index ) {
  if( viewList[index] ) {
    if( viewList[index] != undefined ) {
      // Get current view
      const currentView = Diagram_getCurrentView();
			// Go to new view
			Diagram_setCurrentView( viewList[index] );
			// Store in 0 previous view;
			viewList[0] = currentView;
		}
  }
}
function Diagram_getCurrentView() {
  if( myDiagram ) {
  	// Get current position
  	const position = myDiagram.position;
  	// Get current zoom scale
  	const scale = myDiagram.scale;
  	// Get grid visibility
  	const isGridOn = myDiagram.grid.visible;
  	// Define view info
  	const viewInfo = {
  		position: {
  			x: position.x,
  			y: position.y,
  		},
  		scale,
  		isGridOn,
  	};
	  return( viewInfo );
  } else {
    return( {} );
  }
}
function Diagram_setCurrentView( viewInfo ) {
	if( viewInfo.scale ) {
		// Restore first scale (must be first)
		myDiagram.scale = viewInfo.scale;
	}
	if( viewInfo.position ) {
		// Restore position
		myDiagram.position = new go.Point( viewInfo.position.x, 
		                                   viewInfo.position.y );
	}
	if( typeof( viewInfo.isGridOn ) == 'boolean' ) {
		// Restore grid
		myDiagram.grid.visible = viewInfo.isGridOn;
	}
}
