function generationRoot( startNodeData, onGenerated ) {
  console.log( 'Generating...' );

  // Setup graph information
  if( !graphData.startNodeData ) {
    // Generation Info
    graphData.startNodeData = startNodeData;
    graphData.onGenerated = onGenerated;
    // Initialize graph info
    graphData.g = getMainGraph();
    graphData.modelId = 'main';
  }

  // Info already set, let's start
  startGeneration();
}
function startGeneration() {
  // Initialize tempalte generator
  if( !graphData.lbg ) {
    graphData.lbg = new LineBlockGenerator();
  }

  // Reset counters
  graphData.count = {
    fileCount: 0,
    dirCount: 0,
    nodeCount: 0,
  };

  // Initialize model navigation
  const model = graphData.g.getJSONModel();
  graphData.me = new ModelExplorer();
  graphData.me.setJSONModel( graphData.modelId, model );

  // Initialize Template Manager
  graphData.lb = new LineBlockGenerator();

  // Get deployment node
  graphData.isOverwrite = graphData.me.getProperty( graphData.modelId, 
                                                    graphData.startNodeData, 
                                                    'isOverwrite', null, true );

  // Get output link from name port
  const outDataList = graphData.me.getNodeListFanOutByNodeKey( graphData.modelId, 
                                                               graphData.startNodeData.key );

  // Loop over out nodes
  for( const outData of outDataList ) {
    if( outData.isGroup ) {
      if( !Engine_isNodeEnabled( outData ) ) {
        continue;
      }
    }
    // Count dir
    ++graphData.count.dirCount;
    // Generate group
    generateGroup( outData );
  }

  // Simulation of done
  setTimeout( generationDone, 2*1000 );
}
function generationDone() {
  if( graphData.onGenerated ) {
    const msg = `Dirs: ${graphData.count.dirCount} - `+
                `Files: ${graphData.count.fileCount} - `+
                `Nodes: ${graphData.count.nodeCount}`;
    graphData.onGenerated( msg );
  }
}","fileTypeName":"Javascript","fileType":"text/javascript","key":12,"location":"1790 1050","isIncludeScript":true,"editorPosition":[15,785,592,1289]},
