function CodeFlow_Code_doCompute( data ) {
  // Get node instance
  const ni = Engine_getNodeInstance( data );
  // Make sure all input have a value
  Engine_setInputDefault( data, null );

  // Process template
  graphData.lbg.setLanguage( 'text' );
  const template = data.fileContent;
  graphData.lbg.setTemplate( template );
  graphData.lbg.process( ni.input );

  // Generate all outputs
  const outNameList = Engine_getOutputNameList( data, true );
  for( const outName of outNameList ) {
    const value = graphData.lbg.extractLineBlock( outName, true ); // Trim indentation
    Engine_fireOutput( data, outName, value );
  }
}
function CodeFlow_Merge_doCompute( data ) {
  const value = [];
  // Get node instance
  const ni = Engine_getNodeInstance( data );
  // Get list of input names
  const inNameList = Engine_getInputNameList( data );
  for( const inName of inNameList ) {
    if( ni.input[inName] ) {
      value.push( ...ni.input[inName] );
    }
  }

  Engine_fireOutput( data, 'out', value );
}
function Hierarchy_CodeInFile_doCompute( data ) {
  if( Engine_isInputConnected( data ) ) {
    // Make sure all input have a value
    Engine_setInputDefault( data, '' );
    // Get input value
    const value = Engine_getInput( data, '*', '' );
    // Get a copy of the data (avoid changing original data)
    const dataCopy = getNodeData( data.key, true );
    // Set file content to be saved
    dataCopy.fileContent = ( Array.isArray( value )? value.join( '\n' ): value );
    saveNodeContent( dataCopy, ()=> console.log( '***Data Saved***' ) );
  }
}
function Hierarchy_CodeInGraph_doCompute( data ) {
  Hierarchy_CodeInFile_doCompute( data );
}
