{"label":"Diagram Div Page","color":"lightsalmon","size":"213 89","category":"Hierarchy_CodeInGraph","isFile":true,"fileContent":"<!DOCTYPE html>
<html>
<head>
  <meta charset='utf-8'>
  <title>Test WebPage</title>
  <style>
    /*[# Begin section #]*/
    .diagramDiv {
      position: absolute;
      /* top: -70px;   /* TODO: Remove this line, remove last lines */
      width: 100%;
      /* height: 120%; /* TODO: Restore to 100%
       remove also last lines in htmlMenu.js in function _computeRootMenuMouseClickXY */
      height: 100%;
      background: rgb(160, 160, 160);
    }
    /*[# End section #]*/
  </style>
  <!--GoJS Editor Includes-->
  <script type='text/javascript' src='/library/gojs/2.3.3/go.js'></script>
  <script type='text/javascript' src='/library/gojs/PolylineLinkingTool.js'></script>
  <script type='text/javascript' src='/library/gojs/SnapLinkReshapingTool.js'></script>
  <script type='text/javascript' src='/library/gojs/InGroupClickCreatingTool.js'></script>
  <!--End-->
  <script>

    window.addEventListener( 'load', ()=>{
      // Set initialization function
      document.body.style.setProperty( 'background', '#1d1f21', 'important' ); 
      init();
    });

    //[# Begin pDeclaration #]
    //[# End pDeclaration #]

    const p = {
    //[# Begin tDeclaration #]
      diagram: null,
    //[# End tDeclaration #]
    }

    function init() {
      //[# Begin scriptOnLoad #]
      Diagram_init( 'diagramDiv' );
      Diagram_setup();
      Diagram_setDSL();
      //[# End scriptOnLoad #]
    }
    //[# Begin section #]
    const $ = go.GraphObject.make;  // for conciseness in defining templates
    function Diagram_init( divId ) {

      const diagram = $(go.Diagram, divId,  // create a Diagram for the DIV HTML element
      {
        "undoManager.isEnabled": true,
      });

      // create the model data that will be represented by Nodes and Links
      diagram.model = new go.GraphLinksModel(
        [
          { "key": "Alpha", "color": "#f38181" },
          { "key": "Beta",  "color": "#eaffd0" },
          { "key": "Gamma", "color": "#95e1d3" },
          { "key": "Delta", "color": "#fce38a" }
        ],
        [
          { "from": "Alpha", "to": "Beta" },
          { "from": "Alpha", "to": "Gamma" },
          { "from": "Beta",  "to": "Beta" },
          { "from": "Gamma", "to": "Delta" },
          { "from": "Delta", "to": "Alpha" }
        ]
      );

      State_set( 'diagram', diagram );
    }
    function Diagram_setup() {
      // Diagram Setup of Properties
    }
    function Diagram_setDSL() {
      // DSL definition
      const d = State_get( 'diagram' );
      if( d ) {
        // define a simple Node template (but use the default Link template)
        d.nodeTemplate = $(go.Node, "Auto",
          //{ contextMenu: myContextMenu },
          $(go.Shape, "RoundedRectangle",
            {
              click: setRandomColor,
            },
            // Shape.fill is bound to Node.data.color
            new go.Binding("fill", "color")
          ),
          $(go.TextBlock,
            { 
              margin: 3,
            },  // some room around the text
            // TextBlock.text is bound to Node.data.key
            new go.Binding("text", "key")
          )
        );
      }
    }
    //[# End section #]
    function State_set( name, value ) {
      if( p[name] !== undefined ) {
        p[name] = value;
      } else if( t[name] !== undefined ) {
        t[name] = value;
      } else {
        console.error( `Error: field ${name} not found`)
      }
    }
    function State_get( name ) {
      if( p[name] !== undefined ) {
        return( p[name] );
      } else if( t[name] !== undefined ) {
        return( t[name] );
      } else {
        console.error( `Error: field ${name} not found`)
      }
    }
    function setRandomColor() {
      const colorList = [ 'red',     'yellow',  'green',   'blue', 
                          "#fce38a",  '#95e1d3', '#95e1d3', '#eaffd0' ];

      const idx = Math.floor( Math.random()*colorList.length );
      const d = State_get( 'diagram' );
      changeColor( d, colorList[idx] );
    }
    // A custom command, for changing the color of the selected node(s).
    function changeColor(diagram, color) {
      // Always make changes in a transaction, except when initializing the diagram.
      diagram.startTransaction("change color");
      diagram.selection.each(function(node) {
        if (node instanceof go.Node) {  // ignore any selected Links and simple Parts
          // Examine and modify the data, not the Node directly.
          var data = node.data;
          // Call setDataProperty to support undo/redo as well as
          // automatically evaluating any relevant bindings.
          diagram.model.setDataProperty(data, "color", color);
        }
      });
      diagram.commitTransaction("change color");
    }
  </script>
<body>
  <!--[# Begin section #]-->
  <!--div id="diagram" class='diagramDiv' ondrop="dropHandler(event)" ondragover="dragOverHandler(event)"-->
  <div id="diagramDiv" class='diagramDiv'>
  </div>
  <!--[# End section #]-->
</body>
</html>","fileTypeName":"Web Page","fileType":"application/html","key":115,"location":"2350.0339999999997 4624.412","group":117,"editorPosition":[2911,687,400,350]},
