  <script type='text/javascript' src='/library/gojs/SnapLinkReshapingTool.js'></script>
  <script type='text/javascript' src='/library/gojs/InGroupClickCreatingTool.js'></script>
  <!--End-->
  <!--WinBox Editor Includes-->
  <link rel="stylesheet" href="/library/winbox/0.2.73/modern.min.css">
  <link rel="stylesheet" href="/library/winbox/0.2.73/white.min.css">
  <script type="text/javascript" src="/library/winbox/0.2.73/winbox.bundle.min.js"></script>
  <!--[# End include #]-->

  <style>
    .diagramDiv {
      position: absolute;
      /* top: -70px;   /* TODO: Remove this line, remove last lines */
      width: 100%;
      /* height: 120%; /* TODO: Restore to 100%
       remove also last lines in htmlMenu.js in function _computeRootMenuMouseClickXY */
      height: 100%;
      background: rgb(160, 160, 160);
    }
  </style>

  <script>
    var myDiagram = null;

    window.addEventListener( 'load', ()=>{
      // Set initialization function
      document.body.style.setProperty( 'background', 'lightgray', 'important' ); 
      init();
    });

    function init() {
      Diagram_init( 'diagram' );
    }
    function Diagram_init( divId ) {
      var $ = go.GraphObject.make;  // for conciseness in defining templates

      myDiagram = $(go.Diagram, divId,  // create a Diagram for the DIV HTML element
      {
        "undoManager.isEnabled": true
      });

      // define a simple Node template (but use the default Link template)
      myDiagram.nodeTemplate = $(go.Node, "Auto",
        //{ contextMenu: myContextMenu },
        $(go.Shape, "RoundedRectangle",
          {
            doubleClick: openNode,
          },
          // Shape.fill is bound to Node.data.color
          new go.Binding("fill", "color")
        ),
        $(go.TextBlock,
          { 
            margin: 3,
          },  // some room around the text
          // TextBlock.text is bound to Node.data.key
          new go.Binding("text", "key")
        )
      );

      // create the model data that will be represented by Nodes and Links
      myDiagram.model = new go.GraphLinksModel(
        [
          {
            "key": "Alpha",
            "color": "#f38181",
            "isFile": true,
            "fileType": "application/html",
            "fileURL": "https://en.wikipedia.org/wiki/Alpha"
          },
          { 
            "key": "Beta",
            "color": "#eaffd0",
            "isFile": true,
            "fileType": "application/html",
            "fileURL": "https://en.wikipedia.org/wiki/Beta"
          },
          {
            "key": "Gamma",
            "color": "#95e1d3",
            "isFile": true,
            "fileType": "application/html",
            "fileURL": "https://en.wikipedia.org/wiki/Gamma"
          },
          { "key": "Delta", "color": "#fce38a" }
        ],
        [
          { "from": "Alpha", "to": "Beta" },
          { "from": "Alpha", "to": "Gamma" },
          { "from": "Beta",  "to": "Beta" },
          { "from": "Gamma", "to": "Delta" },
          { "from": "Delta", "to": "Alpha" }
        ]
      );
    }
    function openNode( e, node ) {
      if( node ) {
        const data = node.part.data;
        if( data.fileURL ) {
          const winInfo = { title: data.label, background: data.color, 
                            class: 'modern', url: data.fileURL,
                            x: 10,  y: 10,  width: 250, height: 150 };
          new WinBox( winInfo );
        } else if( data.fileContent ) {

        }
      }
    }
  </script>
<body>
  <div id="diagram" class='diagramDiv'></div>
  <div id='windowDiv'></div>
</body>
