{"label":"Window Div Page","color":"lightsalmon","size":"213 84","category":"Hierarchy_CodeInGraph","isFile":true,"fileContent":"<!DOCTYPE html>
<html>
<head>
  <meta charset='utf-8'>
  <title>Test WebPage</title>
  <style>
  </style>
  <!--Contextify Includes-->
  <script src="https://kit.fontawesome.com/df7df855c2.js" crossorigin="anonymous"></script>
  <script src="/library/Contextify/1.0.1/Contextify.js" id="Contextify"> </script>
  <!--End-->
  <script>

    window.addEventListener( 'load', ()=>{
      // Set initialization function
      document.body.style.setProperty( 'background', '#1d1f21', 'important' ); 
      onLoad();
    });

    const p = {
      //[# Begin pDeclaration #]
      //[# End pDeclaration #]
    };

    const t = {
      //[# Begin tDeclaration #]
      contextMenu: null,
      mainMenu:
      [
        { 
          icon: 'fa-home', 
          type: 'button', 
          hotkey: 'Ctrl + 1', 
          text: 'Home', 
          enabled: false 
        },
        { type: 'separator'},
        { 
          icon: 'fa-share', 
          type: 'button', 
          text: 'Social', 
          child: [
            { 
              icon: 'fa-twitter', 
              type: 'button', 
              hotkey: 'Alt + 1', 
              text: 'Twitter', 
              click: showSelection,
            },
            { 
              icon: 'fa-facebook', 
              type: 'button', 
              hotkey: 'Alt + 1', 
              text: 'Facebook', 
              click: showSelection
            },
            { 
              icon: 'fa-instagram', 
              type: 'button', 
              hotkey: 'Alt + 1', 
              text: 'Instagram', 
              click: showSelection,
            },
          ],
        },
        { 
          icon: 'fa-times-circle', 
          type: 'button', 
          text: 'Cancel', 
          click: showSelection,
        }
      ]
      //[# End tDeclaration #]
    };

    function onLoad() {
      //assignTheme(themeName);
      //[# Begin scriptOnLoad #]
      const contextMenu = new Contextify( 'contextMenuDiv' );
      const mainMenu = State_get( 'mainMenu' );
      contextMenu.setMenu( mainMenu, 'dark' );
      State_set( 'contextMenu', contextMenu );
      //[# End scriptOnLoad #]

      // TODO: this set do not have efects...to chek why!!!
      //contextMenu.setProperty( 'isAutoHide', false ); // disable auto hide

      //contextMenu.hide( hideParent=true|false );
      //[# Begin registerMenu #]
      document.addEventListener( "contextmenu", function(e) {
        e.preventDefault();
        const contextMenu = State_get( 'contextMenu' );
        contextMenu.show( e.x, e.y );
      });
      document.addEventListener( "click", function(e) {
        const contextMenu = State_get( 'contextMenu' );
        contextMenu.hide( true );
      });
      //[# End registerMenu #]
    }

    //[# Begin section #]
    //[# End section #]

    function State_set( name, value ) {
      if( p[name] !== undefined ) {
        p[name] = value;
      } else if( t[name] !== undefined ) {
        t[name] = value;
      } else {
        console.error( `Error: field ${name} not found`)
      }
    }
    function State_get( name ) {
      if( p[name] !== undefined ) {
        return( p[name] );
      } else if( t[name] !== undefined ) {
        return( t[name] );
      } else {
        console.error( `Error: field ${name} not found`)
      }
    }
    // Get selection element
    function showSelection( event ) {
      const selectionEl = document.getElementById( 'selection' );
      selectionEl.innerText = event.item.text;
    }
  </script>
<body>
  <!--[# Begin section #]-->
  <div id="contextMenuDiv" style="position: relative; z-index: 10000;"></div>
  <!--[# End section #]-->
  <h3 style='color: LightGray;'>Just right click to open and click to close</h3>
  <div style='color: Yellow; display: inline;'>Selection: </div>
  <div id="selection" style='color: LightGray; display: inline;'></div>
</body>
</html>","fileTypeName":"Web Page","fileType":"application/html","key":163,"location":"2320 5960","group":160,"editorPosition":[1966,1225,400,350]},
