function Diagram_zoomToFit() {
  const d = State_get( 'diagram' );
  if( d ) {
    const viewList = State_get( 'viewList' );
    if( viewList ) {
      // Store last view in ViewLast
  		viewList[0] = Diagram_getCurrentView();
    }
		// Zoom to fit
    d.zoomToFit();
  }
}
function Diagram_storeView( index ) {
  const viewList = State_get( 'viewList' );
  if( viewList ) {
    viewList[index] = Diagram_getCurrentView();
  }
}
function Diagram_goToView( index ) {
  const viewList = State_get( 'viewList' );
  if( viewList && viewList[index] ) {
    if( viewList[index] != undefined ) {
      // Get current view
      const currentView = Diagram_getCurrentView();
			// Go to new view
			Diagram_setCurrentView( viewList[index] );
			// Store in 0 previous view;
			viewList[0] = currentView;
		}
  }
}
function Diagram_getCurrentView() {
  const d = State_get( 'diagram' );
  if( d ) {
  	// Get current position
  	const position = d.position;
  	// Get current zoom scale
  	const scale = d.scale;
  	// Get grid visibility
  	const isGridOn = d.grid.visible;
  	// Define view info
  	const viewInfo = {
  		position: {
  			x: position.x,
  			y: position.y,
  		},
  		scale,
  		isGridOn,
  	};
	  return( viewInfo );
  } else {
    return( {} );
  }
}
function Diagram_setCurrentView( viewInfo ) {
  const d = State_get( 'diagram' );
  if( d && viewInfo ) {
    if( viewInfo.scale ) {
    	// Restore first scale (must be first)
    	d.scale = viewInfo.scale;
    }
    if( viewInfo.position ) {
    	// Restore position
    	d.position = new go.Point( viewInfo.position.x, 
    	                           viewInfo.position.y );
    }
    if( typeof( viewInfo.isGridOn ) == 'boolean' ) {
    	// Restore grid
    	d.grid.visible = viewInfo.isGridOn;
    }
  }
}
