function generateGroup( gData ) {
  const gDataKey = gData.key;
  const dataList = graphData.me.getNodeListIf( graphData.modelId, 
                                             (d)=> d.group == gDataKey );
  for( const data of dataList ) {
    if( data.isGroup ) {
      if( Engine_isNodeEnabled( data ) ) {
        // Count dir
        ++graphData.count.dirCount;
        // Generate group
        generateGroup( data );
      }
    } else if( data.category.startsWith( 'Hierarchy_CodeIn' ) ) {
      // Generate File

      traverseNode( data );
    }
  }
}
function traverseNode( data ) {
  // Get input link into data node
  const inDataList = graphData.me.getNodeListFanInByNodeKey( graphData.modelId,
                                                             data.key );
  // Loop over in nodes
  for( const inData of inDataList ) {
    if( Engine_isNodeEnabled( inData ) ) {
      traverseNode( inData );
    }
  }

  const isFile = ( data.category.startsWith( 'Hierarchy_CodeIn') );
  if( isFile ) {
    // Count file
    ++graphData.count.fileCount;
  } else {
    // Count nodes
    ++graphData.count.nodeCount;
  }

  // Execute doCompute
  console.log( 'doCompute [', data.key, ']', data.label || data.category );
  Engine_doCompute( data );
}","fileTypeName":"Javascript","fileType":"text/javascript","key":19,"location":"1790 1190","isIncludeScript":true,"editorPosition":[1114,908,636,746]},
