{"label":"Engine Base Library","color":"tan","size":"286 111","category":"Hierarchy_CodeInGraph","isFile":true,"fileContent":"//------------------------------
// Engine Base Library
//------------------------------

// Initialize Engine
if( !graphData.nodeInstance ) {
  Engine_clearNodeInstance();
}

//------------------------------
// Engine Base Library
//------------------------------
function Engine_getNodeInstance( data ) {
  // If node not existing => create it
  if( !graphData.nodeInstance[data.key] ) {
    graphData.nodeInstance[data.key] = {
      property: {},
      input: {},
      inputState: {},
      output: {},
      isCompute: false,
    };
  }
  // Return node instance
  return( graphData.nodeInstance[data.key] );
}
function Engine_clearNodeInstance() {
  // Persistent link data for nodes
  graphData.nodeInstance = {};
  graphData.engineInstance = {
    currNodeSelectionKey: -1, // Animation
  };
}
function Engine_isNodeEnabled( data ) {
  let result = true;
  if( data.isGroup ) {
    result = ( ( data.color == undefined ) || ( data.color.toLowerCase() == 'green' ) );
  } else if( data.group !== undefined ) {
    const gData = graphData.me.getNode( graphData.me.modelId, data.group );
    result = ( gData && ( gData.color.toLowerCase() == 'green' ) );
  }
  return( result );
}
function Engine_getInputLinkDataList( data, name ) {
  const result = [];
  const pushInDataList = ()=> {
    // Get input link from port name
    const inDataList = graphData.me.getLinkListFanInByNodeKey( graphData.modelId, 
                                                     data.key,
                                                     name );
    for( const inData of inDataList ) {
      if( Engine_isNodeEnabled( inData ) ) {
        result.push( inData );
      }
    }
  };

  // Check if name exist
  if( data.in ) {
    for( const inInfo of data.in ) {
      if( inInfo.portId == name ) {
        pushInDataList();
        break;
      }
    }
  } else {
    name = undefined;  // No input name in this case
    pushInDataList();
  }
  return( result );
}
function Engine_getOutputLinkDataList( data, name ) {
  const result = [];
  const pushOutDataList = ()=> {
    // Get output link from port name
    const outDataList = graphData.me.getLinkListFanOutByNodeKey( graphData.modelId,
                                                    data.key,
                                                    name );
    for( const outData of outDataList ) {
      if( Engine_isNodeEnabled( outData ) ) {
        result.push( outData );
      }
    }
  };

  // Check if name exist
  if( data.out ) {
    for( const outInfo of data.out ) {
      if( outInfo.portId == name ) {
        pushOutDataList();
        break;
      }
    } 
  } else {
    name = undefined; // No input name in this case
    pushOutDataList();
  }
  return( result );
}
function Engine_fetchInputs( data ) {
  const storeValue = ( data, fromInName, toInName, defaultValue )=> {
    const inLinkList = graphData.me.getLinkListFanInByNodeKey( graphData.modelId,
                                                            data.key,
                                                            fromInName );
    for( const inLink of inLinkList ) {
      // Get source node
      const outData = graphData.me.getNode( graphData.me.modelId, inLink.from );

      if( Engine_isNodeEnabled( outData ) ) {
        const value = Engine_getOutput( outData, inLink.fromPort, defaultValue );
        Engine_setInput( data, toInName, value );
        break;
      }
    }
  }
  // Loop over all connected inputs
  const inNameList = Engine_getInputNameList( data, true );
  // Case of component with input ports
  if( inNameList.length ) {
    for( const inName of inNameList ) {
      // Store in input inName the first enabled link value comming into it
      storeValue( data, inName, inName, null );
    }
  } else {
    // Case of component without input ports
    // Store in input '*' the first enabled link value comming into it
    storeValue( data, undefined, '*', undefined );
  }
}
function Engine_isInputConnected( data, name ) {
  // Get input link list
  const list = Engine_getInputLinkDataList( data, name );
  // If input is connected
  return( list && list.length )
}
function Engine_isOutputConnected( data, name ) {
  // Get output link list
  const list = Engine_getOutputLinkDataList( data, name );
  // If output is connected
  return( list && list.length )
}
function Engine_getInputNameList( data, isOnlyConnected ) {
  const result = []
  if( data.in ) {
    for( const inInfo of data.in ) {
      const name = inInfo.portId;
      // If we want only connected one => skip unconnected one
      if( ( isOnlyConnected != undefined ) && isOnlyConnected ) {
        if( !Engine_isInputConnected( data, name ) ) {
          continue;
        }
      }
      result.push( name );
    }
  }
  return( result );
}
function Engine_getOutputNameList( data, isOnlyConnected ) {
  const result = []
  if( data.out ) {
    for( const outInfo of data.out ) {
      const name = outInfo.portId;
      // If we want only connected one => skip unconnected one
      if( ( isOnlyConnected != undefined ) && isOnlyConnected ) {
        if( !Engine_isOutputConnected( data, name ) ) {
          continue;
        }
      }
      result.push( name );
    }
  }
  return( result );
}","fileTypeName":"Javascript","fileType":"text/javascript","key":30,"location":"2230 1050","isIncludeScript":true,"editorPosition":[1756,909,703,1167]},
{"label":"Engine Base API","color":"tan","size":"286 111","category":"Hierarchy_CodeInGraph","isFile":true,"fileContent":"//------------------------------
// Engine Base API
//------------------------------
function Engine_get( data, name, defaultValue ) {
  // Get node instance
  const ni = Engine_getNodeInstance( data );

  // Set default value if property did not exist
  if( ni.property[name] === undefined ) {
    ni.property[name] = defaultValue;
  }
  // Return property value
  return( ni.property[name] );
}
function Engine_set( data, name, value ) {
  // Get node instance
  const ni = Engine_getNodeInstance( data );

  // Set property value
  ni.property[name] = value;
}
function Engine_getInput( data, name, defaultValue ) {
  // Get node instance
  const ni = Engine_getNodeInstance( data );

  // Update default value
  if( ni.input[name] == undefined ) {
    ni.input[name] = defaultValue;
  }
  return( ni.input[name] );
}
function Engine_setInput( data, name, value ) {
  // Get node instance
  const ni = Engine_getNodeInstance( data );

  // Update value
  ni.input[name] = value;
  // Update state
  if( !ni.inputState[name] ) {
    ni.inputState[name] = {};
  }
  ni.inputState[name].isNew = true;
}
function Engine_setInputDefault( data, defaultValue ) {
  // Get node instance
  const ni = Engine_getNodeInstance( data );
  // Get list of input names
  const inNameList = Engine_getInputNameList( data );
  for( const inName of inNameList ) {
    if( ni.input[inName] == undefined ) {
      ni.input[inName] = defaultValue;
    }
  }
}
function Engine_getLanguage( data ) {
  let result = 'text';
  if( data.fileType ) {
    const [format, language] = data.fileType.split( '/' );
    result = language;
  }
  return( result );
}","fileTypeName":"Javascript","fileType":"text/javascript","key":33,"location":"2230 1330","isIncludeScript":true,"editorPosition":[3268,773,528,1240]},
{"label":"Engine Compute Library","color":"tan","size":"286 111","category":"Hierarchy_CodeInGraph","isFile":true,"fileContent":"//------------------------------
// Engine Compute Library
//------------------------------
function Engine_getOutput( data, name, defaultValue ) {
  let result = defaultValue;
  // Get node instance
  const ni = Engine_getNodeInstance( data );
  if( ni.output[name] != undefined ) {
    result = ni.output[name];
  }
  return( result );
}
function Engine_getComputeBarrier( data ) {
  let result = [];
  const cb = graphData.me.getProperty( graphData.modelId, 
                                       data,
                                       'computeBarrier',
                                       null );
  if( cb ) {
    result = cb.split( ',' ).map( n => n.trim() );
  } else {
    result = Engine_getInputNameList( data );
  }
  return( result );
}
function Engine_updateInput( data, name, value ) {
  // Get node instance
  const ni = Engine_getNodeInstance( data );
  // If doCompute just executed => reset inputs isNew state
  if( ni.isCompute ) {
    for( const inName in ni.inputState ) {
      ni.inputState[inName].isNew = false;
    }
  }

  // Set new input value
  Engine_setInput( data, name, value );

  let isCompute = true;
  // Get input names for barrier
  const inNameList = Engine_getComputeBarrier( data );
  for( const inName of inNameList ) {
    if( Engine_isInputConnected( data, inName ) ) {
      let isNewInput = false;
      if( ni.inputState[inName] ) {
        isNewInput = ni.inputState[inName].isNew;
      }
      isCompute &&= isNewInput;
      // Short cirtuit computation
      if( !isCompute ) {
        break;
      }
    }
  }
  // Update compute status
  ni.isCompute = isCompute;
}
function Engine_fireOutput( data, name, value ) {
  // Fire only connected outputs
  if( Engine_isOutputConnected( data, name ) ) {
    // Get node instance
    const ni = Engine_getNodeInstance( data );

    // Save output
    ni.output[name] = value;
  }
}
function Engine_doCompute( data ) {
  if( globalThis[ `${data.category}_doCompute`] ) {
    // Get all input values
    Engine_fetchInputs( data );
    // Execute compute fungion
    globalThis[ `${data.category}_doCompute`]( data );
  } else {
    console.log( 'Compute not found for', data.category );
  }
}","fileTypeName":"Javascript","fileType":"text/javascript","key":36,"location":"2230 1190","isIncludeScript":true,"editorPosition":[1379,958,704,1069]},
