{"label":"Window Div Page","color":"lightsalmon","size":"213 84","category":"Hierarchy_CodeInGraph","isFile":true,"fileContent":"<!DOCTYPE html>
<html>
<head>
  <meta charset='utf-8'>
  <title>Test WebPage</title>
  <style>
    #contextMenuDiv {
      position: absolute;
      width: 100%;
      height: 100%;
      top: 0px;
      left: 0px;
    }
  </style>
  <!--Contextify Includes-->
  <script src="https://kit.fontawesome.com/df7df855c2.js" crossorigin="anonymous"></script>
  <script src="/library/Contextify/1.0.1/Contextify.js" id="Contextify"> </script>
  <!--End-->
  <script>

    window.addEventListener( 'load', ()=>{
      // Set initialization function
      document.body.style.setProperty( 'background', '#1d1f21', 'important' ); 
      onLoad();
    });

    const p = {
      //[# Begin pDeclaration #]
      //[# End pDeclaration #]
    };

    const t = {
      //[# Begin tDeclaration #]
      c_contextMenu: null,
      c_mainMenuModel:
      [
        { 
          icon: 'fa-home', 
          type: 'button', 
          hotkey: 'Ctrl + 1', 
          text: 'Home', 
          enabled: false 
        },
        { type: 'separator'},
        { 
          icon: 'fa-share', 
          type: 'button', 
          text: 'Social', 
          child: [
            { 
              icon: 'fa-twitter', 
              type: 'button', 
              hotkey: 'Alt + 1', 
              text: 'Twitter', 
              click: showSelection,
            },
            { 
              icon: 'fa-facebook', 
              type: 'button', 
              hotkey: 'Alt + 1', 
              text: 'Facebook', 
              click: showSelection
            },
            { 
              icon: 'fa-instagram', 
              type: 'button', 
              hotkey: 'Alt + 1', 
              text: 'Instagram', 
              click: showSelection,
            },
          ],
        },
        { 
          icon: 'fa-times-circle', 
          type: 'button', 
          text: 'Cancel', 
          click: showSelection,
        }
      ],
      //[# End tDeclaration #]
    };

    function onLoad() {
      //assignTheme(themeName);
      //[# Begin scriptOnLoad #]
      ContextMenu_init();
      //[# End scriptOnLoad #]

      // TODO: this set do not have efects...to chek why!!!
      //c.setProperty( 'isAutoHide', false ); // disable auto hide

      //c.hide( hideParent=true|false );
      //[# Begin registerMenu #]
      ContextMenu__registerMenu();
      //[# End registerMenu #]
    }

    //[# Begin section #]
    function ContextMenu_init() {
      const c = new Contextify( 'contextMenuDiv' );
      const c_mainMenuModel = State_get( 'c_mainMenuModel' );
      c.setMenu( c_mainMenuModel, 'dark' );

      State_set( 'c_contextMenu', c );
    }
    function ContextMenu__registerMenu() {
      const c = State_get( 'c_contextMenu' );
      document.addEventListener( "contextMenu", function(e) {
        e.preventDefault();
        c.show( e.x, e.y );
      });
      document.addEventListener( "click", function(e) {
        c.hide( true );
      });
    }
    //[# End section #]

    function State_set( name, value ) {
      if( p[name] !== undefined ) {
        p[name] = value;
      } else if( t[name] !== undefined ) {
        t[name] = value;
      } else {
        console.error( `Error: field ${name} not found`)
      }
    }
    function State_get( name ) {
      if( p[name] !== undefined ) {
        return( p[name] );
      } else if( t[name] !== undefined ) {
        return( t[name] );
      } else {
        console.error( `Error: field ${name} not found`)
      }
    }
    // Get selection element
    function showSelection( event ) {
      const selectionEl = document.getElementById( 'selection' );
      selectionEl.innerText = event.item.text;
    }
