        const funCall = funcName+params;
        try {
          if( userCmd ) {
            // Execute function
            eval( `out = ${funCall};` );
          }
        } catch( e ) {
          out = 'Command not found';
        }
      } else {
        out = VFS_run( userCmd );
      }
      content.value = content.value+'\n'+
                      `# ${userCmd}\n`+
                      out.join( '\n' );
      content.scrollTop = content.scrollHeight;
      prompt.value = '';
      prompt.focus();
    }
