    //------------------------
    function VFS_run( command ) {
      const out = [];
      const v_cwdObj = State_get( 'v_cwdObj' );
      let isCommandFound = false;

      for( const entry in v_cwdObj ) {
        if( entry.startsWith( '__' ) ) {
          continue;
        }
        // If a directory starts with the itemName
        if( entry.startsWith( command ) && !entry.endsWith( '/' ) ) {
          const entryObj = v_cwdObj[entry];
          if( typeof( entryObj ) == 'function' ) {
            const result = entryObj();
            out.push(...result.split( '\n' ) );
            isCommandFound = true;
            break;
          }
          // // Complete the name
