#statusDiv {
  position: absolute;
  width: 100%;
  height: 100%;
  box-sizing: border-box;
  border: 10px;
  border-style: solid;
  border-color: rgba(125, 125, 125, 0.6);
  z-index: 100;
  pointer-events: none;
}
