{"label":"Window Div Page","color":"lightsalmon","size":"213 84","category":"Hierarchy_CodeInGraph","isFile":true,"fileContent":"<!DOCTYPE html>
<html>
<head>
  <meta charset='utf-8'>
  <title>Test WebPage</title>
  <style>
    .wb-like {
        background-size: 20px auto;
    }
    .wb-like.active {
        background-image: url(/fileServer/pictures/pinned.svg) !important;
    }
  </style>
  <!--WinBox Editor Includes-->
  <link rel="stylesheet" href="/library/winbox/0.2.73/modern.min.css">
  <link rel="stylesheet" href="/library/winbox/0.2.73/white.min.css">
  <script type="text/javascript" src="/library/winbox/0.2.73/winbox.bundle.min.js"></script>
  <!--End-->
  <script>

    //[# Begin scriptOnLoad #]
    //[# End scriptOnLoad #]

    // This function is used to define the output block
    function dummy() {
      //[# Begin scriptOnOpenWindow #]
      Window_openWindow( data, onLoaded, x, y );
      //[# End scriptOnOpenWindow #]
    }
    // This function is used to define the output block
    function dummy2() {
      //[# Begin scriptOnCloseWindow #]
      Window_closeUnpinWindow();
      //[# End scriptOnCloseWindow #]
    }


    //[# Begin section #]
    //------------------------
    // API Functions
    //------------------------
    function Window_alert( msg, isCenter ) 
    {
      isCenter = ( isCenter == undefined? true: isCenter );
      new WinBox( 'Alert', {
        modal: true,
        autosize: true,
        background: 'Crimson',
        html: `<div style="margine: 0px;">`+
                `<pre style="${isCenter? 'text-align: center;':''}">`+
                  msg+
                `</pre>`+
              `</div>`,
      });
    }
    function Window_openWindow( data, onLoaded, x, y ) {
      // If data is defined and can't put win on focus => open new
      if( data && !Window__tryFocusWindow( data ) ) {

        // Get window position (previous or default)
        const winLoc = Window__getLocation( data );
        // Initialize coordinates
        if( x != undefined ) {
          winLoc[0] = x;
        }
        if( y != undefined ) {
          winLoc[1] = y;
        }

        // Create window info
        const winInfo = Window__createWinInfo( data, winLoc );
        // Open a new window
        const win = new WinBox( winInfo );
        Window__customizeWindow( win, { key: data.key } );

        // Store window position and status
        Window__setLocation( data, winLoc );
        Window__setOpenWindow( data, win );
      }
    }
    function Window_tooglePin( data ) {
      if( data ) {
        const dURL = Diagram_getDiagramURL();
        const ol = State_get( 'w_openList' );
        if( !ol[dURL] ) {
          ol[dURL] = {}; 
        }
        const key = data.key;
        ol[dURL][key].isPin = !ol[dURL][key].isPin;
        // TODO: make the icon updated in the window
      }
    }
    function Window_isPin( data ) {
      let result = false;
      if( data ) {
        const dURL = Diagram_getDiagramURL();
        const key = data.key;
        const ol = State_get( 'w_openList' );
        if( ol[dURL] && ol[dURL][key] ) {
          result = ol[dURL][key].isPin;
        }
      }
      return( result );
    }
    function Window_getWindow( data ) {
      // TODO: return the window managing the data
      //       It should be possible to apply
      //       w = Window_getWindow( data )
      //       src = Window_getSource( w )
    }
    function Window_closeUnpinWindow() {
      const dURL = Diagram_getDiagramURL();
      const wl = State_get( 'w_winList' );
      const winList = wl[dURL];
      if( winList ) {
        for( const key in winList ) {
          const win = winList[key];
          if( win ) {
            const data = { key };
            if( !Window_isPin( data ) ) {
              win.close();
            }
          }
        }
      }
    }
    function Window_closeAllWindow() {
      const dURL = Diagram_getDiagramURL();
      const wl = State_get( 'w_winList' );
      const winList = wl[dURL];
      if( winList ) {
        for( const key in winList ) {
          const win = winList[key];
          if( win ) {
            win.close();
          }
        }
      }
    }
    //[# End section #]

    const p = {
      //[# Begin pDeclaration #]
      w_positionList: {
        // url1: {
        //   key1: [x, y, width, height],
        //   key2: [x, y, width, height],
        // },
      },
      w_openList: {
        // url1: {
        //  key1: { isPin: true },
        //  key1: { isPin: false },
        // }
      },
      //[# End pDeclaration #]
    };
    const t = {
      //[# Begin tDeclaration #]
      w_winList: {
        // url1: {
        //   key1: win, 
        // }
      },
      //[# End tDeclaration #]
    };

    //[# Begin scriptOnLoad #]
    //[# End scriptOnLoad #]

    //[# Begin section #]
    //------------------------
    // Internal Functions
    //------------------------
    function Window__getLocation( data ) {
      let result = [10, 10, 250, 200]; // Default window pos/size
      if( data ) {
        const dURL = Diagram_getDiagramURL();
        const pl = State_get( 'w_positionList' );
        let winList = pl[dURL];
        if( winList ) {
          const key = data.key;
          const winPos = winList[key];
          if( winPos ) {
            result = winPos;
          }
        }
      }
      return( result );
    }
    function Window__setLocation( data, winPos ) {
      if( data ) {
        const dURL = Diagram_getDiagramURL();
        const pl = State_get( 'w_positionList' );
        if( !pl[dURL] ) {
          pl[dURL] = {};
        }
        const key = data.key;
        pl[dURL][key] = winPos;
      }
    }
    function Window__setPosition( data, x, y ) {
      if( data ) {
        const dURL = Diagram_getDiagramURL();
        const pl = State_get( 'w_positionList' );
        if( !pl[dURL] ) {
          pl[dURL] = {};
        }
        const key = data.key;
        if( pl[dURL][key] ) {
          pl[dURL][key][0] = x;
          pl[dURL][key][1] = y;
        } else {
          pl[dURL][key] = [x, y, 250, 200];
        }
      }
    }
    function Window__setSize( data, w, h ) {
      if( data ) {
        const dURL = Diagram_getDiagramURL();
        const pl = State_get( 'w_positionList' );
        if( !pl[dURL] ) {
          pl[dURL] = {};
        }
        const key = data.key;
        if( pl[dURL][key] ) {
          pl[dURL][key][2] = w;
          pl[dURL][key][3] = h;
        } else {
          pl[dURL][key] = [10, 10, w, h];
        }
      }
    }
    function Window__setOpenWindow( data, win ) {
      if( data ) {
        const dURL = Diagram_getDiagramURL();
        const ol = State_get( 'w_openList' );
        if( !ol[dURL] ) {
          ol[dURL] = {}; 
        }
        const key = data.key;
        ol[dURL][key] = { isPin: false };

        const wl = State_get( 'w_winList' );
        if( !wl[dURL] ) {
          wl[dURL] = {}; 
        }
        wl[dURL][key] = win;
      }
    }
    function Window__tryFocusWindow( data ) {
      let result = false;
      if( data ) {
        const dURL = Diagram_getDiagramURL();
        const wl = State_get( 'w_winList' );
        const winList = wl[dURL];
        if( winList ) {
          const key = data.key;
          const win = winList[key];
          if( win ) {
            win.focus( true );
            result = true;
          }
        }
      }
      return( result );
    }
    function Window__createWinInfo( data, winLoc ) {
      // Set window property
      const title = data.label
      const url = data.fileURL;
      const wClass = 'modern'; // class is a reserved keyword
      const background = data.color || 'Plum';

      // Window info
      const winInfo = { 
        title, background, class: wClass,
        x: winLoc[0],     y: winLoc[1],
        width: winLoc[2], height: winLoc[3],
        onmove: (x,y)=>{
          Window__setPosition( data, x, y );
        },
        onresize: (w,h)=>{
          Window__setSize( data, w, h );
        },
        onclose: (e)=>{
          Window__removeWindow( data );
        },
      };

      if( data.fileURL ) {
        winInfo.url = data.fileURL;
      } else if( data.fileContent ) {

      }

      return( winInfo );
    }
    function Window__customizeWindow( win, dataKey ) {
      win.removeControl("wb-max");
      win.addControl({
          index: 2,
          class: "wb-like",
          image: "/fileServer/pictures/unpinned.svg",
          click: function( event, winbox ) {
              // the winbox instance will be passed as 2nd parameter
              // console.log(winbox.id);
              // "this" refers to the button which was clicked:
              this.classList.toggle( "active" );
              Window_tooglePin( dataKey );
          }
      });
    }
    function Window__removeWindow( data ) {
      if( data ) {
        const dURL = Diagram_getDiagramURL();
        // Remove from winList
        const wl = State_get( 'w_winList' );
        const winList = wl[dURL];
        if( winList ) {
          const key = data.key;
          if( winList[key] ) {
            delete winList[key];
          }
        }
        // Remove from openList
        const ol = State_get( 'w_openList' );
        const openList = ol[dURL];
        if( openList ) {
          const key = data.key;
          if( openList[key] ) {
            delete openList[key];
          }
        }
      }
    }
    //[# End section #]

    window.addEventListener( 'load', ()=>{
      // Set initialization function
      document.body.style.setProperty( 'background', '#1d1f21', 'important' ); 
      onLoad();
    });

    const dataList = [
      { key: 0, label: 'Node Zero',  fileURL:'https://en.wikipedia.org/wiki/Alpha', color: '#ff005d' },
      { key: 1, label: 'Node One',   fileURL:'https://en.wikipedia.org/wiki/Beta', color: 'Yellow' },
      { key: 2, label: 'Node Two',   fileURL:'https://en.wikipedia.org/wiki/Gamma', color: 'LightGreen' },
      { key: 3, label: 'Node Three', fileURL:'https://en.wikipedia.org/wiki/Delta', color: 'Green' },
    ];

    function onLoad() {
    }
    function Window_openXY( e, key ) {
      Window_openWindow( dataList[key], null, e.offsetLeft, e.offsetTop );
    }
    function Window_open( e, key ) {
      Window_openWindow( dataList[key], null );
    }

    //[# Begin scriptOnLoad #]
    //[# End scriptOnLoad #]

    //[# Begin section #]
    //[# End section #]

    function Diagram_getDiagramURL() {
      return( 'localhost' );
    }
    function State_set( name, value ) {
      if( p[name] !== undefined ) {
        p[name] = value;
      } else if( t[name] !== undefined ) {
        t[name] = value;
      } else {
        console.error( `Error: field ${name} not found`)
      }
    }
    function State_get( name ) {
      if( p[name] !== undefined ) {
        return( p[name] );
      } else if( t[name] !== undefined ) {
        return( t[name] );
      } else {
        console.error( `Error: field ${name} not found`)
